'use strict';

import Core from '../tools/core.js';
import Widget from './widget.js';

/**
 * Application module
 * @module base/Application
 * @extends Widget
 */
export default class Application extends Widget { 
	
	/**
	 * Handles error events for widgets. 
	 * @returns {array} an array of widget objects
	 */
	handle_widget_errors(widgets) {
		widgets.forEach(w => {
			w.on("error", ev => this.handle_error(ev.error));
		});
	}
	
	/**
	 * Handles an error, displays an alert and logs it to console
	 * @returns {object} an error object
	 */
	handle_error(error) {
		alert(error.message);
		console.error(error);
	}
	
	/**
	 * Return an HTML fragment to build the widget's UI
	 * @returns {string} the HTML fragment for the widget's UI
	 */
	html() {
		return	"<div handle='app-container' class='app-container'></div>";
	}
}

'use strict';

import Core from '../tools/core.js';
import Evented from './evented.js';
import Nls from './nls.js';

/**
 * Component module, a base class for localized and evented objects
 * @module base/Component
 * @extends Evented
 */
export default class Component extends Evented { 

	/**                              
	 * Get/set the nls object
	 */
	get nls() { return this._nls; }

	set nls(value) { this._nls = value; }

	/**
	 * Constructor for component object
	 */
	constructor() {
		super();
		
		this._nls = new Nls();
		
		this.localize(this._nls);
	}
	
	/**
	 * Add specified language strings to the nls object
	 * @param {object} nls - Existing nls object
	 */
	localize(nls) {
		return;
	}
	
	/**
	 * @description
	 * Get a localized nls string resource
	 * @param {object} id — the id of the nls resource to retrieve
	 * @param {array} subs - an array of Strings to substitute in the localized nls string resource
	 * @param {string} locale - the locale for the nls resource
	 * @returns - the localized nls string resource
	 */
	nls(id, subs, locale) {
		return this._nls.get(id, subs, locale);
	}
}

'use strict';

import Core from '../tools/core.js';

/**
 * Evented module. Allows the child class to emit and listen to events.
 * @module base/Evented
 */
export default class Evented { 

	/**
	 * Constructor for evented object
	 */
	constructor() {
		this._listeners = {};
	}

	/**
	 * @description
	 * Add an event listener to the list of event listeners.
	 * @param {string} type - event type to be listened for
	 * @param {function} callback - a callback function that listens for an event
	 * @param {boolean} once - indicates whether the event should be listened to only once
	 * @returns {object} an object containing the event handler information
	 */
	add_event_listener(type, callback, once){
		if (!(type in this._listeners)) this._listeners[type] = [];
		
		var h = { target:this, type:type, callback:callback, once:!!once };
		
		this._listeners[type].push(h);
		
		return h;
	}
	
	/**
	 * @description
	 * Remove an event listener from the list of event listeners.
	 * @param {string} type - name of the event
	 * @param {function} callback - a callback function that listens for an event
	 */
	remove_event_listener(type, callback){
		if (!(type in this._listeners)) return;
	  
		var stack = this._listeners[type];
		  
		for (var i = 0, l = stack.length; i < l; i++){
			if (stack[i].callback === callback){
				stack.splice(i, 1);
				
				return this.remove_event_listener(type, callback);
			}
		}
	}
	
	/**
	 * @description
	 * Invoke an event listener.
	 * @param {object} event - an event object 
	 */
	dispatch_event(event){
		if (!(event.type in this._listeners)) return;

		var stack = this._listeners[event.type];

		for (var i = 0; i < stack.length; i++) {
			stack[i].callback.call(this, event);
		}
		
		for (var i = stack.length - 1; i >= 0; i--) {
			if (!!stack[i].once) this.remove_event_listener(event.type, stack[i].callback);
		}
	}
	
	/**
	 * @description
	 * Dispatches an event from this class
	 * @param {string} type - a string containing the name of the event
	 * @param {object} data - an object to be passed with the event
	 */
	emit(type, data) {
		// Let base event properties be overwritten by whatever was provided.	
		var event = { bubbles:true, cancelable:true };
	
		Core.mixin(event, data);
		
		// Use the type that was specifically provided, target is always this.
		event.type = type;
		event.target = this;
		
		this.dispatch_event(event);
	}
	
	/**
	 * @description
	 * Add a an event listener
	 * @param {string} type - event type to be listened for
	 * @param {function} callback - a callback function that executes when the event is heard
	 * @returns {object} an object containing the event handler information
	 */
	on(type, callback) {
		return this.add_event_listener(type, callback, false);
	}

	/**
	 * @description
	 * Add a one time event listener
	 * @param {string} type - event type to be listened for
	 * @param {function} callback - a callback function that executes when the event is heard
	 * @returns {object} an object containing the event handler information
	 */
	once(type, callback) {
		return this.add_event_listener(type, callback, true);
	}

	/**
	 * @description
	 * Remove an event listener
	 * @param {string} type - event type to be listened for
	 * @param {function} callback - a callback function that listens for an event
	 */
	off(type, callback) {
		this.remove_event_listener(type, callback);
	}
}

'use strict';

/**
 * List module. An array extension with an index for finding items easily.
 * @module base/List
 */
export default class List extends Array { 
	/**                              
	 * Get/set the indexing function
	 */
	set fn_key(value) { this._fn_key = value; }
	get fn_key() { return this._fn_key; }

	/**                              
	 * Get/set the index associated to the list
	 */
	set index(value) { this._index = value; }
	get index() { return this._index; }

	/**                              
	 * Get the first item in the list
	 */
	get first() { return this[0]; }

	/**                              
	 * Get the last item in the list
	 */
	get last() { return this[this.length - 1]; }

	/**
	 * Constructor for list object
	 * @param {function} fn_key - indexing function for the list
	 * @param {array} items - items in the list
	 */
	constructor(fn_key, items) {
		super();

		this.fn_key = fn_key;
		this.index = {};
		
		if (items) items.forEach(i => this.add(i));
	}

	/**
	 * @description
	 * Adds an item to the list
	 * @param {object} item - an item to add to the list
	 * @returns {object} the added item or the preexisting item 
	 */
	add(item) {
		if (!this.fn_key) {
			this.push(item);
			
			return item;
		}
		
		var key = this.fn_key(item);
		
		if (!this.has_key(key)) {
			this.index[key] = item;
			this.push(item);
		} 

		return this.index[key];
	}
	
	/**
	 * @description
	 * Removes an item from the list
	 * @param {object} item - an item to remove from the list
	 * @returns {object} the removed item
	 */
	remove(item) {
		var key = this.fn_key(item);
		var idx = this.get_index(item);
		var g = this[idx];
		
		this.splice(idx, 1);
		delete(this.index[key]);
		
		return g;
	}

	/**
	 * @description
	 * Gets an item from the list
	 * @param {string} key - the key of the item to retrieve
	 * @returns {object} the item if found, null otherwise
	 */
	get(key) {
		return this.index[key] || null;
	}
	
	/**
	 * @description
	 * Gets the index of an item from the list
	 * @param {object} item - the item for which to retrieve the index
	 * @returns {number} the index of the item found, -1 otherwise
	 */
	get_index(item) {
		var key = this.fn_key(item);
		
		return this.indexOf(this.index[key]);
	}

	/**
	 * @description
	 * Checks if the list contains an item
	 * @param {object} item - the item to check
	 * @returns {boolean} true if the item is found, false otherwise
	 */
	has(item) {
		var key = this.fn_key(item);

		return this.has_key(key);
	}

	/**
	 * @description
	 * Checks if the list contains an item key
	 * @param {string} key - the key to look for
	 * @returns {boolean} true if the item is found, false otherwise
	 */
	has_key(key) {
		return key in this.index;
	}

	/**
	 * @description
	 * Rebuilds the index for the list, this may be necessary after mapping the list.
	 * @param {function} fn_key - the function used to build the index
	 * @returns {boolean} true if the item is found, false otherwise
	 */
	reindex(fn_key) {
		this.index = {};
		this.fn_key = fn_key;
		
		for (var i = 0; i < this.length; i++) {
			var key = this.fn_key[this[i]];
			
			this.index[key] = this[i];
		}
		
		return this;
	}
	
	/**
	 * @description
	 * Maps the list to another list using a delegate function
	 * @param {function} fn_map - the function used to map the list
	 * @returns {object} the new list
	 */
	map(fn_map) {
		return new List(null, super.map(fn_map));
	}
	
	/**
	 * @description
	 * Filters the list to another list using a delegate function
	 * @param {function} fn_filter - the function used to filter the list
	 * @returns {object} the new list
	 */
	filter(fn_filter) {
		return new List(this.fn_key, super.filter(fn_filter));
	}
		
	/**
	 * @description
	 * Sorts the list in place using a delegate function in ascending or descending order
	 * @param {function} fn_value - the function used to get the values to sort
	 * @param {boolean} ascending - true to sort in ascending order, false for descending
	 */
	sort(fn_value, ascending) {		
		super.sort((a, b) => {
			var vA = fn_value(a);
			var vB = fn_value(b);
			
			if (vA == vB) return 0;
			if (vA == null) return 1;
			if (vB == null) return -1;
			
			if (ascending) return vA > vB ? 1 : -1;
			
			else return vA > vB ? -1 : 1;
		});
	}
}

'use strict';

import Core from '../tools/core.js';

/**
 * Nls module, an object that holds multi lingual strings
 * @module base/Nls
 */
export default class Nls { 

	/**
	 * Constructor for the nls object
	 * @param {string} strings - an object containing the english and french strings. 
	 *							 each object should be { id:"", en:"", fr:"" }
	 */
	constructor(strings) {
		this.strings = {};
		
		if (!strings) return;
		
		strings.forEach(s => this.add(s[0], s[1], s[2]));
	}
	
	/**
	 * @description
	 * Get a localized nls string resource
	 * @param {String} id - the id of the nls resource to retrieve
	 * @param {Array} subs - an array of Strings to substitute in the localized nls string resource
	 * @param {String} locale - the locale for the nls resource (optional)
	 * @returns the localized nls string resource
	 */
	get(id, subs, locale) {
		if (!this.strings) throw new Error("Nls content not set.");
		
		var itm = this.strings[id];

		if (!itm) throw new Error("Nls String '" + id + "' undefined.");

		var txt = itm[(locale) ? locale : Core.locale];
		
		if (!txt) throw new Error("String does not exist for requested language.");

		return this.format(txt, subs);
	}
	
	/**
	 * @description
	 * Adds a localized string
	 * @param {String} id - the id of the nls resource to add
	 * @param {String} locale - the locale for the string added
	 * @param {String} string - the string to add
	 */
	add(id, locale, string) {
		if (!this.strings[id]) this.strings[id] = {};
		
		this.strings[id][locale] = string;
	}
		
	/**
	 * @description
	 * Formats a String using substitute string
	 * @param {String} str - The String to format
	 * @param {Array} subs - An array of Strings to substitute into the String
	 * @returns the formatted String
	 */
	format(str, subs) {
		if (!subs || subs.length == 0) return str;
		
		var s = str;

		for (var i = 0; i < subs.length; i++) {
			var reg = new RegExp("\\{" + i + "\\}", "gm");
			s = s.replace(reg, subs[i]);
		}

		return s;
	}
};

'use strict';

import Core from '../tools/core.js';
import Dom from '../tools/dom.js';

/**
 * Template module
 * @module base/Template
 */
/**
 * Template module, an object used to template widgets objects
 * @module base/Template
 */
export default class Template { 

	/**
	 * Get the root element of the template
	 */
	get root() { return this._root; }

	/**
	 * Get the DOM elements of the template
	 */
	get elems() { return this._elems; }
	
	/**
	 * Get / set the html string for the template
	 */
	get html() { return this._html; }

	set html(value) { this._html = value.trim(); }

	/**
	 * Constructor for template object
	 * @param {string} html - a string containing the HTML fragment
	 */
	constructor(html) {		
		
		this._elems = {};
	}
	
	/**
	 * Builds the template from the HTML fragment, replaces any nls in the fragment.
	 * @param {string} html - a string containing the HTML fragment
	 * @param {string} nls - an nls object containing strings for multi language support
	 */
	build(html, nls) {	
		if (!nls) this.html = html;
			
		// Replace all nls strings in template. Nls string pattern in templates is nls(StringId)
		else this.html = this.replace(html, /nls\((.*?)\)/, m => nls.get(m));
		
		this._template = Dom.create("div", { innerHTML:this.html });
		
		this.set_named_nodes();
		this.set_root();
		this.set_subwidgets();
	}
	
	/**
	 * Sets nodes with handles on the template. Any node with a handle attribute will 
	 * be stored in the elems property.
	 */
	set_named_nodes() {		
		var named = this._template.querySelectorAll("[handle]");
		
		// Can't use Array ForEach here since named is a NodeList, not an array
		for (var i = 0; i < named.length; i++) { 
			var name = Dom.get_attribute(named[i], "handle");
			
			this._elems[name] = named[i];
		}
	}
	
	/**
	 * Sets the root element for the template
	 */
	set_root() {
		this._root = null;
		
		if (this._template.children.length == 0) return;
		
		if (this._template.children.length > 1) {
			throw new Error("templates can only have one child element, verify the HTML function.");
		}
		
		this._root = this._template.children[0];
	}
	
	/**
	 * Sets the subwidgets for the template. Replaces any node with a widget attribute 
	 * by an instance of the widget.
	 */
	set_subwidgets() {		
		var nodes = this._template.querySelectorAll("[widget]");
		
		// Can't use Array ForEach here since nodes is a NodeList, not an array
		for (var i = 0; i < nodes.length; i++) {
			var path = Dom.get_attribute(nodes[i], "widget");
			var module = Core.templatable(path);
			var widget = new module();
			
			// replace div provided by widget root
			var next = nodes[i].nextElementSibling;
			
			if (!next) nodes[i].parentElement.appendChild(widget.root);
			
			else nodes[i].parentElement.insertBefore(widget.root, next);
			
			// store handle in nodes to access widget later
			var handle = Dom.get_attribute(nodes[i], "handle");
			
			if (handle) this._elems[handle] = widget;
			
			// transfer attributes from div provided to widget root
			for (var j = 0; j < nodes[i].attributes.length; j++) {	
				var attr = nodes[i].attributes[j];
				
				if (attr.name == 'handle' || attr.name == 'widget') continue;
				
				widget.root.setAttribute(attr.name, attr.value);		
			}
			
			// use innerHTML as content for widget
			if (nodes[i].innerHTML.length > 0) widget.content = nodes[i].innerHTML;
			
			nodes[i].remove();
		}
	}
	
	/**
	 * Places the built fragment in a container DOM element.
	 * @param {object} container - a dom element
	 */
	place(container) {
		this._container = container;
		
		Dom.place(this.root, this._container);
	}

	
	/**
	 * Replaces substrings in a string using a delegate function
	 * @param {string} str - the string in which to replace substrings
	 * @param {string} expr - the expression to replace
	 * @param {function} delegate - the delegate function used to replace content
	 * @returns {string} the modified string
	 */
	replace(str, expr, delegate) {
		var m = str.match(expr);
		
		while (m) {
			str = str.replace(m[0], delegate(m[1]));
			m = str.match(expr);
		}
		
		return str;
	}
}

'use strict';

import Component from './component.js';
import Template from './template.js';
import Dom from '../tools/dom.js';

/**
 * Widget module, a base class for complex UI components
 * @module base/Widget
 * @extends Component
 */
export default class Widget extends Component { 
	
	/**
	 * Get/set the template object
	 */
	get template() { return this._template; }
	set template(value) { this._template = value; }

	/**
	 * Get/set the container DOM Element
	 */
	get container() { return this._container; }
	set container(value) { 
		this._container = value;
		
		if (this.container) this.template.place(this.container); 
	}

	/**
	 * Set the content of this widget
	 */
	set content(value) {
		throw new Error("content setter not defined.");
	}
	
	/**
	 * Get the root element of the widget
	 */
	get root() { return this.template.root; }
	
	/**
	 * Get the DOM elements of the widget
	 */
	get elems() { return this.template.elems; }

	/**
	 * Constructor for widget object
	 * @param {object} container - A DOM element that will contain the widget
	 */
	constructor(container) {
		super();
		
		var html = this.html();
	
		if (html) {
			this.template = new Template(html);
			
			this.template.build(html, this._nls);
		};
		
		this.container = container;
	}
	
	/**
	 * Return an HTML fragment to build the widget's UI
	 * @returns {string} the HTML fragment for the widget's UI
	 */
	html() {
		return null;		
	}
	
	/**
	 * Handles error by emitting an event containing the error. This error should
	 * be caught at the application level.
	 * @returns {string} the HTML fragment for the widget's UI
	 */
	error(error) {
		this.emit("error", { error:error });
	}
}

'use strict';

import Core from '../tools/core.js';
import Dom from '../tools/dom.js';
import Component from '../base/component.js';

/**
 * A base class used to link a simulation with a widget
 */
export default class Automator extends Component { 
	/**                              
	 * Get the simulation object
	 */	
	get simulation() { return this._simulation; }

	/**                              
	 * Get the widget linked to the simulation
	 */	
	get widget() { return this._widget; }

	/**
	 * @param {Widget} widget - a widget to link to the simulation
	 * @param {Simulation} simulation - a simulation object
	 */	
	constructor(widget, simulation) {
		super();

		this._simulation = simulation;
		this._widget = widget;
		this._handles = [];
	}

	/**
	 * Adds handled events to the automator
	 * @param {object[]} handles - an array of event handles
	 */	
	handle(handles) {
		this._handles = this._handles.concat(handles);
	}

	/**
	 * Destroys the automator component. Deattach all handlers.
	 */	
	destroy() {
		this.widget.root.remove();

		this._handles.forEach((h) => {
			h.target.off(h.type, h.callback);
		});

		this._simulation = null;
		this._widget = null;
		this._handles = null;
	}

	/**
	 * Redraws the widget, must be inherited and overwritten
	 */	
	redraw() {

	}
};

'use strict';

import Core from '../tools/core.js';
import Evented from '../base/evented.js';

const CHUNK_SIZE = 8388608;

/**
 * A component that can read files by chunks. It wraps a FileReader object.
 */
export default class Reader extends Evented { 

	constructor () {
		super();

		this.fileReader = new FileReader();
		this.defer = null;

		this.fileReader.addEventListener("loadend", this.on_load_end.bind(this));
		this.fileReader.addEventListener("error", this.on_load_error.bind(this));
	}

	/**
	 * Read a file asynchronously, meant to be private.
	 * @param {File} file - the file to read
	 * @return {promise} a promise, resolved when the file is read
	 */	
	promise_read(file) {
		if (this.defer) throw new Error("FileReader is in use");

		this.defer = Core.defer();

		// let start = reverse ? file.size - CHUNK_SIZE : 0;
        // let end = reverse ? file.size : CHUNK_SIZE;
        // let slice = file.slice(start, end);

		// TODO: readAsArrayBuffer is better for large files supposedly
		this.fileReader.readAsText(file);
		// this.fileReader.readAsArrayBuffer(file);

		return this.defer.promise;
	}

	/**
	 * Handles the load event on the internal FileReader object
	 * Resolves the promise for the chunk reader
	 * @param {event} ev - event object
	 */
	on_load_end(ev) {
		var resolve = this.defer.Resolve;

		this.defer = null;

		resolve(this.fileReader.result);
	}

	/**
	 * Handles errors on the FileReader object
	 * @param {event} ev - event object
	 */
	on_load_error(ev) {		
		// TODO: This never triggers, example, bad visualization.json file
		var reject = this.defer.Reject;

		this.defer = null;

		reject(new Error("Unable to read the file."));
	}

	/**
	 * Read a file asynchronously, processes the file using a delegate function.
	 * @param {File} file - the file to read
	 * @param {Function} delegate - the delegate used to process the response
	 * @return {promise} a promise, resolved when the file is read
	 */	
	read(file, delegate) {
		var d = Core.defer();

		if (!file) return d.Resolve(null);

		this.promise_read(file).then(function(result) {
			try {			
				d.Resolve(delegate(result));
			}
			catch(ex) {
				d.Reject(ex);

				throw(ex);
			}
		}, (error) => { d.Reject(error); });

		return d.promise;
	}

	/**
	 * Read a file asynchronously as json
	 * @param {File} file - the file to read
	 * @return {promise} a promise, resolved when the file is read
	 */	
	read_as_json(file) {
		return this.read(file, json => JSON.parse(json));
	}

	/**
	 * Read a file asynchronously as text
	 * @param {File} file - the file to read
	 * @return {promise} a promise, resolved when the file is read
	 */	
	read_as_text(file) {
		return this.read(file, text => text);
	}

	/**
	 * Read a file asynchronously by chunks, processes content line by line using 
	 * a delegate, incomplete lines are left to the next chunk.
	 * @param {File} file - the file to read
	 * @param {string} split - the line delimiter string
	 * @param {function} delegate - the function used to process each line
	 * @return {promise} a promise, resolved when the file is completely read
	 * @todo this does too many things. There should be one function for chunking
	 *		 processing line by line shouldn't be here.
	 */	
	read_by_chunk(file, split, delegate) {
		var position = 0;
		var d = Core.defer();
		var read = null;

		if (!file) return d.Resolve(null);

		var ReadChunk = (size) => {
			var chunk = file.slice(position, position + size);

			this.promise_read(chunk).then((result) => {
				var idx = size > result.length ? result.length - 1 : result.lastIndexOf(split);
				var content = result.substr(0, idx);

				position += content.length + 1;

				try {
					read = delegate(read, content, 100 * position / file.size);
				}
				catch (error) {
					d.Reject(error);
				}

				if (position < file.size) ReadChunk(size);

				else if (position == file.size) d.Resolve(read);

				else d.Reject(new Error("Reader position exceeded the file size."));
			});
		}

		ReadChunk(CHUNK_SIZE);

		return d.promise;
	}

	/**
	 * Read a file asynchronously, processes the file using a delegate function. 
	 * This function is static to avoid instantiating the chunk reader.
	 * @param {File} file - the file to read
	 * @param {Function} delegate - the delegate used to process the response
	 * @return {promise} a promise, resolved when the file is read
	 */	
	static Read(file, delegate) {
		var reader = new Reader();

		return reader.read(file, delegate);
	}

	/**
	 * Read a file asynchronously by chunks, processes content line by line using 
	 * a delegate, incomplete lines are left to the next chunk.
	 * This function is static to avoid instantiating the chunk reader.
	 * @param {File} file - the file to read
	 * @param {string} split - the line delimiter string
	 * @param {function} delegate - the function used to process each line
	 * @return {promise} a promise, resolved when the file is completely read
	 * @todo this does too many things. There should be one function for chunking
	 *		 processing line by line shouldn't be here.
	 */	
	static read_by_chunk(file, split, delegate) {
		var reader = new Reader();

		return reader.read_by_chunk(file, split, delegate);
	}

	/**
	 * Read a file asynchronously as json
	 * This function is static to avoid instantiating the chunk reader.
	 * @param {File} file - the file to read
	 * @return {promise} a promise, resolved when the file is read
	 */	
	static read_as_json(file) {
		var reader = new Reader();

		return reader.read_as_json(file);
	}

	/**
	 * Read a file asynchronously as text
	 * This function is static to avoid instantiating the chunk reader.
	 * @param {File} file - the file to read
	 * @return {promise} a promise, resolved when the file is read
	 */	
	static read_as_text(file) {
		var reader = new Reader();

		return reader.read_as_text(file);
	}
}

