'use strict';

import Style from "../../tools/style.js";
import Widget from '../../base/widget.js';
import Dom from '../../tools/dom.js';

/** 
 * A legend widget for OpenLayers maps. Wrapper around an ol.control.Legend
 **/
export default class Legend extends Widget { 

	/** 
	* Gets the OL control underlying the legend
	* @type {number} 
	*/
	get control() { return this._control; }

	constructor() {		
		super();

		this._control = new ol.control.Control({ element: this.elems.legend_container });
	}

    /**
     * Adds a legend section for a visual variable
	 * @param {object} variable - the visual variable
     */
	add_legend(variable) {
		if (variable.style.fill) {
			var rows = this.build_fill(variable.style.fill);

			this.add_legend_rows(variable.layer, `${variable.style.fill.property}`, rows);
		}

		if (variable.style.radius) {
			var rows = this.build_radius(variable.style.radius);

			this.add_legend_rows(variable.layer, `${variable.style.radius.property}`, rows);
		}
		/*
		if (variable.style.src) {
			var rows = this.build_scale(variable.style.scale, variable.style.src);

			this.add_legend_rows(variable.layer, `${variable.style.scale.property}`, rows);
		}
		*/
	}

    /**
     * Adds the legend rows 
	 * @param {object} layer - a layer configuration object
	 * @param {string} property - the property used for the visual variable
	 * @param {object[]} rows - the rows for the legend
     */
	add_legend_rows(layer, property, rows) {
		var sizes = rows.map(r => r.size);
		var size = Math.max(...sizes) * 2;

		var legend = new ol.control.Legend({ title:`${layer.label} - ${property}`, margin:5, size:[size, size], collapsed:false, target:this.control.element });

		rows.forEach(r => {
			legend.addRow({ title:r.title, typeGeom:"Point", style:r.style });
		});

		Dom.place(legend.element, this.control.element);
	}

    /**
     * Adds the zero value legend row
	 * @param {object[]} rows - the rows for the legend
	 * @param {object} buckets - the buckets used for a visual variable
	 * @param {string} color - the zero color
     */
	add_zero(rows, buckets, color) {
		for (var i = 0; i < buckets.length; i++) {
			if (buckets[i] > 0) break;
		}

		rows.splice(i, 0, {
			title: "0", 
			size: 8, 
			style: Style.point_style({
				radius: 8, 
				stroke: { color: "#000", width: 1 },
				fill: { color: color }
			})
		});
	}

    /**
     * Builds legend rows for a fill style
	 * @param {StaticFill, BucketFill} style - the fill style 
	 * @return {object[]} the legend rows for the legend
	 * @todo This should be in the style classes directly
     */
	build_fill(style) {
		var prev = null;

		var rows = style.buckets.map((b, i) => {
			var curr = b.toFixed(4).toString();
			var title = (prev) ? `${prev} - ${curr}` : `0 - ${curr}`;

			prev = curr;

			return {
				title: title, 
				size: 8, 
				style: Style.point_style({
					radius: 8, 
					stroke: { color: "#000", width: 1 },
					fill: { color: style.colors[i] }
				})
			}
		});

		if (style.zero) this.add_zero(rows, style.buckets, style.zero);

		return rows;
	}

    /**
     * Builds legend rows for a radius style
	 * @param {StaticRadius|BucketRadius} style - the radius style 
	 * @return {object[]} the legend rows for the legend
	 * @todo This should be in the style classes directly
     */
	build_radius(style){
		var prev = null;

		var rows = style.buckets.map((b, i) => {			
			var curr = b.toFixed(4).toString();
			var title = (prev) ? `${prev} - ${curr}` : `0 - ${curr}`;

			prev = curr;

			return {
				title: title, 
				size: style.radius[i], 
				style: Style.point_style({
					radius: style.radius[i], 
					stroke: { color: "#000", width: 1 } ,
					fill: { color: "#fff" }
				})
			}
		});

		if (style.zero) this.add_zero(rows, style.buckets, style.zero);

		return rows;
	}

    /**
     * Builds legend rows for a scale style
	 * @param {StaticScale|BucketScale} style - the scale style 
	 * @param {string} src - the source for the icon
	 * @return {object[]} the legend rows for the legend
	 * @todo This should be in the style classes directly
     */
	build_scale(style, src){
		var prev = null;

		var rows = style.buckets.map((b, i) => {
			var curr = b.toFixed(4).toString();
			var title = (prev) ? `${prev} - ${curr}` : `0 - ${curr}`;

			prev = curr;

			return {
				title: title, 
				size: style.scale[i], 
				style: Style.point_icon_style({
					scale: style.scale[i], 
					src: src
				})
			}
		});

		if (style.zero) this.add_zero(rows, style.buckets, style.zero);

		return rows;
	}

	/**
	 * Create HTML for select element
	 * @returns {string} HTML for select element
	 */
	html() {
		return "<div handle='legend_container' class='ol-control ol-legend-container'></div>";
	}
}

import Evented from '../../base/evented.js';
import Widget from '../../base/widget.js';

/** 
 * A map component that wraps an OpenLayers maps (ol.Map)
 **/
export default class Map extends Evented {
	/** 
	* Gets the OL control underlying the map
	* @type {number} 
	*/
	get OL() { return this._ol; }

	/** 
	* Gets the map layers as a map of string and layers
	* @type {object} 
	*/
	get layers() { return this._layers; }

    /**
	 * @param {object} container - div container
	 * @param {ol.layer.Tile[]} basemaps - the basemaps to use
     */
	constructor(container, basemaps) {
		super(); 

		this._layers = {};

		var sl = new ol.control.ScaleLine();

		this.basemaps = basemaps;

		this._ol = new ol.Map({
			target: container,
			layers: [new ol.layer.Group({
				title: 'Basemaps',
				layers: basemaps
			})],
			controls: ol.control.defaults({ attributionOptions: { collapsible: true } }).extend([sl]),
		});

		this._ol.on("click", (ev) => {
			var features = [];

			this._ol.forEachFeatureAtPixel(ev.pixel, function (feature, layer) {
				features.push({ layer:layer.get('title'), feature:feature });
			});

			this.emit("click", { "features" : features, "coordinates" : ev.coordinate });
		})

		this._ol.once("rendercomplete", (ev) => {
			this.emit("render-complete", null);
		});

		this.projection = basemaps[0].getSource().getProjection();

		this.popup = new ol.Overlay.Popup({ closeBox:true });

		this.OL.addOverlay(this.popup);
	}

    /**
     * Get a layer by id
	 * @param {string} id - the layer id
	 * @return {ol.layer.Layer} the layer corresponding to the id
     */
	layer(id) {
		return this.layers[id];
	}

    /**
     * Get the features associated to a layer by id
	 * @param {string} id - the layer id
	 * @return {ol.Feature[]} an array of features from the layer
     */
	layer_features(id) {
		return this.layer(id).getSource().getFeatures();
	}

    /**
     * Adds controls to the map
	 * @param {ol.control.Control[]} controls - an array of controls to add to the map
     */
	add_control(controls) {
		if (!Array.isArray(controls)) controls = [controls];

		controls.forEach(c => this.OL.addControl(c));
	}

    /**
     * Removes controls to the map
	 * @param {ol.control.Control[]} controls - an array of controls to remove from the map
     */
	remove_control(controls) {
		if (!Array.isArray(controls)) controls = [controls];

		controls.forEach(c => this.OL.removeControl(c));
	}

    /**
     * Adds a layer to the map
	 * @param {string} id - the layer id
	 * @param {ol.layer.Layer} layer - the layer to add
	 * @return {ol.layer.Layer} the added layer
     */
	add_layer(id, layer) {
		this.OL.addLayer(layer);

		this.layers[id] = layer;

		return layer;
	}

    /**
     * Adds a layer to the map from geojson
	 * @param {string} id - the layer id
	 * @param {object} json - a geojson set of features
	 * @return {ol.layer.Layer} the added layer
     */
	add_geojson_layer(id, json) {			
		var format = new ol.format.GeoJSON({ featureProjection : this.projection });
		var vs = new ol.source.Vector({features: format.readFeatures(json)});

		return this.add_layer(id, new ol.layer.Vector({ source: vs, title: json.name  }));
	}

    /**
     * Adds a layer to the map from features
	 * @param {string} id - the layer id
	 * @param {object} features - an array of ol.Feature
	 * @param {string} title - the layer title
	 * @param {object} style - the layer style
	 * @return {ol.layer.Layer} the added layer
     */
	add_vector_layer(id, features, title, style) {
		// TODO: Not sure about the wrapX thing.
		var source = new ol.source.Vector({ features:features, wrapX:false });

		var vector = new ol.layer.Vector({ source:source, title:title, style:style });

		return this.add_layer(id, vector);
	}

    /**
     * Sets the map view, zoom and center
	 * @param {number[]} coord - an array of X,Y coordinates
	 * @param {number} zoom - the zoom level
     */
	set_view(coord, zoom) {
		this.OL.setView(new ol.View({
			center: ol.proj.transform(coord, "EPSG:4326", "EPSG:900913"),
			zoom: zoom,
		}));
	}

    /**
     * Shows an infopopup on the map
	 * @param {number[]} coord - an array of X,Y coordinates
	 * @param {Widget|string} content - the content of the infopopup
     */
	show_popup(coord, content) {
        this.popup.setPosition(coord);

		if (content instanceof Widget) {
			this.popup.show(coord, "");

			content.container = this.popup.content;
		}

		else this.popup.show(coord, content);
	}

    /**
     * Returns an OpenStreetMap basemap
	 * @param {boolean} visible - basemap visibility
	 * @return {ol.layer.Tile} the basemap created
     */
	static basemap_osm(visible) {
		return new ol.layer.Tile({ 
			title: "OpenStreetMap",
			source: new ol.source.OSM(),
			visible: !!visible,
			baseLayer: true
		});
	}

    /**
     * Returns a Satellite imagery basemap
	 * @param {boolean} visible - basemap visibility
	 * @return {ol.layer.Tile} the basemap created
     */
	static basemap_satellite(visible) {
		return new ol.layer.Tile({ 
			title: "Satellite",
			source: new ol.source.XYZ({
				// attributions: ['Powered by Esri',
				// 			   'Source: Esri, DigitalGlobe, GeoEye, Earthstar Geographics, CNES/Airbus DS, USDA, USGS, AeroGRID, IGN, and the GIS User Community'],
				attributionsCollapsible: false,
				url: 'https://services.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
				maxZoom: 23
			}),
			visible: !!visible,
			baseLayer: true
		});
	}
}

'use strict';

import Core from '../tools/core.js';
import Evented from "../base/evented.js";
import Reader from "../components/chunk-reader.js";
import Configuration from '../data_structures/configuration/configuration.js';
import Structure from "../data_structures/metadata/structure.js";
import MessageState from "../data_structures/simulation/message_state.js"
import MessageOutput from "../data_structures/simulation/message_output.js"
import MessageStateCA from "../data_structures/simulation/message_state_ca.js"
import Frame from "../data_structures/simulation/frame.js";

/**
 * A parser component to process the common results format
 */
export default class Parser extends Evented { 
	/**                              
	 * Parses the style.json file
	 * @param {File} fStyle - the style.json file
	 * @return {object} the json content
	 */		
	async parse_style(fStyle) {
		return await Reader.read_as_json(fStyle);		
	}

	/**                              
	 * Parses the visualization.json file
	 * @param {File} fViz - the visualization.json file
	 * @return {object} the json content
	 */		
	async parse_visualization(fViz) {
		return await Reader.read_as_json(fViz);
	}

	/**                              
	 * Parses the structure.json file
	 * @param {File} fStruct - the structure.json file
	 * @return {Structure} the structure object built from the file
	 */		
	async parse_structure(fStruct) {
		var json = await Reader.read_as_json(fStruct);

		return Structure.from_json(json);
	}

	/**                              
	 * Parses the diagram.svg file
	 * @param {File} fDiag - the diagram.svg file
	 * @return {string} a string containing the diagram content
	 */		
	async parse_diagram(fDiag) {
		return await Reader.read_as_text(fDiag);
	}

	/**                              
	 * Parses the messages.log file
	 * @param {Structure} structure - the structure object for a simulation
	 * @param {File} file - the messages.log file
	 * @return {Frame[]} an array of frames built from the messages.log
	 */		
	async parse_messages(structure, file) {
		this.frame = null;

		var parseFn = structure.info.type == "Cell-DEVS" ? this.parse_line_ca : this.parse_line;

		return Reader.read_by_chunk(file, "\n", (parsed, chunk, progress) => {
			if (parsed == null) parsed = [];

			// If line has only one item, then it's a timestep. Otherwise, it's a simulation message, 
			// the format then depends on the whether it's a DEVS, Cell-DEVS or Irregular model
			var lines = chunk.split("\n");

			for (var i = 0; i < lines.length; i++) {
				var data = lines[i].trim().split(";");

				if (data.length == 1) {
					this.frame = new Frame(data[0]);

					parsed.push(this.frame);
				}

				else {					
					data = data.map(d => d.split(","));

					parseFn(this.frame, structure, data[0], data[1]);
				}
			}

			this.emit("progress", { progress: progress });

			return parsed;
		});
	}

	/**                              
	 * Parses a line from the messages.log. Adds it to the frame provided.
	 * @param {Frame} frame - a time frame for the simulation
	 * @param {Structure} structure - the structure object for a simulation
	 * @param {string} emitter - the emitter of the message
	 * @param {string} values - the value contained in the message
	 */	
	parse_line(frame, structure, emitter, values) {
		var model = structure.models[emitter[0]];
		var port = emitter.length == 2 ? model.ports[emitter[1]]: null ; 

		if (port) {
			var values = port.apply_template(values);

			frame.add_output_message(new MessageOutput(model, port, values));
		}

		else frame.add_state_message(new MessageState(model, model.apply_template(values)));	
	}

	/**                              
	 * Parses a CA simulation line from the messages.log. Adds it to the frame provided. 
	 * @param {Frame} frame - a time frame for the simulation
	 * @param {Structure} structure - the structure object for a simulation
	 * @param {number[]} coord - the coordinate of the emitter
	 * @param {number[]} values - the values contained in the message
	 */	
	parse_line_ca(frame, structure, coord, values) {		
		var values = structure.model_types[1].apply_template(values);

		frame.add_state_message(new MessageStateCA(coord, values));
	}
}

'use strict';

import Core from '../tools/core.js';
import Evented from "../base/evented.js";
import Reader from "../components/chunk-reader.js";
import List from "../base/list.js";
import Configuration from '../data_structures/configuration/configuration.js';
import SimulationCA from "../data_structures/simulation/simulation_ca.js";
import Frame from "../data_structures/simulation/frame.js";
import MessageStateCA from "../data_structures/simulation/message_state_ca.js"
import TypeModelCA from '../data_structures/metadata/type_model_ca.js';
import TypePort from '../data_structures/metadata/type_port.js';
import ModelCA from '../data_structures/metadata/model_ca.js';
import MessageType from '../data_structures/metadata/message_type.js';
import Structure from "../data_structures/metadata/structure.js";
import Info from "../data_structures/metadata/info.js";

/**
 * A parser component to process the raw Cell-DEVS results from CDpp or Lopez
 */
export default class ParserCDpp extends Evented { 

	constructor() {
		super();

		this.initialValue = null;
		this.initialRowValues = null;
	}

	/**                              
	 * Parses a component token from the ma file into a structure model. 
	 * @param {Structure} structure - the simulation structure
	 * @param {string} name  - the name of the model
	 * @param {string} type - the type of the model
	 * @return {Model} the model created
	 */	
	make_component(structure, name, type) {
		var msg_type_id = structure.message_types.length;
		var model_type_id = structure.model_types.length;

		var msg_type = new MessageType(msg_type_id, `s_${name}`, ["out"], "No description available.");
		var port = new TypePort("out", "output", null);
		var model_type = new TypeModelCA(model_type_id, name, type, msg_type, [port], null, null, null)
		var model = new ModelCA(name, model_type, null);

		structure.message_types.push(msg_type);
		structure.model_types.push(model_type);

		structure.add_model(model);

		return model;
	}

	/**                              
	 * Parses the *.ma file into a Structure object
	 * @param {File} ma - the *.ma file
	 * @return {Structure} the structure object built from the file
	 */		
	async parse_structure(ma) {	
		var s = new Structure(new Info("top", "CDpp", "Cell-DEVS"));	

		var id = 0;
		var content = await Reader.read_as_text(ma); 
		var lines = content.split("\n");
		var curr = null;

		for (var i = 0; i < lines.length; i++) {
			var l = lines[i].trim();

			if (l.startsWith("[")) {		
				var name = l.substring(1, l.length - 1);

				if (name.toLowerCase() == "top") this.make_component(s, name, "coupled");

				curr = s.model_types.find(m => m.name == name);
			}

			else if (curr == null || l.startsWith('%') || !l.includes(":")) continue;

			else {
				var kv = l.split(':').map(l => l.trim());

				kv[0] = kv[0].toLowerCase();

				if (kv[0] == "components") {				
					var model = this.make_component(s, kv[1].split(' ')[0].trim(), "atomic");

					curr.add_submodel(model); 
				}

				else if (kv[0] == "dim") {
					curr.dim = kv[1].substring(1, kv[1].length - 1).split(",").map(c => +c.trim());

					if (curr.dim.length == 2) curr.dim.push(1);
				}

				else if (kv[0] == "height") {
					if (!curr.dim) curr.dim = [0, +kv[1], 1];

					else curr.dim[1] = +kv[1];
				}

				else if (kv[0] == "width") {
					if (!curr.dim) curr.dim = [+kv[1], 0, 1];

					else curr.dim[0] = +kv[1];
				}

				else if (kv[0] == "neighborports") {
					var ports = kv[1].split(" ").map(c => c.trim());

					ports.forEach(p => {
						curr.add_port(new TypePort(`out_${p}`, "output", null));
						curr.template.push(`out_${p}`);
					});
				}

				else if (kv[0] == "initialvalue") this.initialValue = kv[1];

				else if (kv[0] == "initialrowvalue") {
					if (this.initialRowValues == null) this.initialRowValues = [];

					var lr = kv[1].replace(/\s\s+/g, ',').split(',');

					this.initialRowValues.push({
						row: +lr[0],
						values: lr[1].split('')
					});
				}

			}
		}

		return s;
	}

	/**                              
	 * Parses the *.pal file
	 * @param {File} pal - the *.pal file
	 * @return {object} the style object
	 */	
	async parse_palette(pal) {
		var content = await Reader.read_as_text(pal);		
		var lines = content.trim().split("\n").map(l => l.trim());
		var style = { buckets: [] };

		// VALIDSAVEDFILE
		// 169,169,169
		// ...
		// 4.0,4.9
		if (lines[0] == "VALIDSAVEDFILE") {
			var j = 0;

			for (var i = 1; i < lines.length; i++) {
				var split = lines[i].split(',').map(l => +l.trim());

				if (split.length == 3) {
					style.buckets.push({ start:null, end:null, color:split });
				}

				else {
					style.buckets[j].start = split[0];
					style.buckets[j++].end = split[1];
				}
			}
		}

        // [-0.1;0.9] 255 255 51
        // [-1.1;-0.9] 153 255 255
        // ...
		else {
			for (var i = 0; i < lines.length; i++) {
				split = lines[i].replace('[','')
								.replace(']','')
								.replace(';',' ')
								.replaceAll(',','')
								.split(' ').map(s => +s);

				style.buckets.push({ start:split[0], end:split[1], color:[split[2], split[3], split[4]] });
			}
		}

		return [style];
	}

	/**                              
	 * Parses the initialrowvalues token in the ma file. 
	 * Adds corresponding messages to the frame 0.
	 * @param {Frame[]} frames - the simulation frames
	 * @param {string} t0  - the time 0 (i.e. 00:00:000)
	 * @param {Model} model - the top model of the simulation 
	 */	
	read_initial_row_values(frames, t0, model) {
		if (this.initialRowValues == null) return;

		var f0 = frames.get(t0) || frames.add(new Frame(t0));

		this.initialRowValues.forEach(irv => {
			irv.values.forEach((v, y) => {
				var coord = [irv.row, +y, 0]
				var value = model.template.map(t => v);

				f0.add_state_message(new MessageStateCA(coord, value));
			});
		});
	}

	/**                              
	 * Parses the initialvalues token in the ma file. 
	 * Adds corresponding messages to the frame 0.
	 * @param {Frame[]} frames - the simulation frames
	 * @param {string} t0  - the time 0 (i.e. 00:00:000)
	 * @param {Model} model - the top model of the simulation 
	 * @param {number[]} dim - the dimensions of the cell-space
	 */	
	read_initial_value(frames, t0, model, dim) {
		if (this.initialValue == null) return;

		var f0 = frames.get(t0) || frames.add(new Frame(t0));

        for (var x = 0; x < model.dim[0]; x++) {
            for (var y = 0; y < model.dim[1]; y++) {
                for (var z = 0; z < model.dim[2]; z++) {
					var coord = [x, y, z];
					var value = model.template.map(t => this.initialValue);

					f0.add_state_message(new MessageStateCA(coord, value));
				}
			}
		}		
	}

	/**                              
	 * Parses the *.val file. Adds corresponding messages to the frame 0.
	 * @param {Frame[]} frames - the simulation frames
	 * @param {string} t0  - the time 0 (i.e. 00:00:000)
	 * @param {File} val - the *.val file
	 */	
	async read_val(frames, t0, val) {
		if (!val) return;

		var f0 = frames.get(t0) || frames.add(new Frame(t0));
		var content = await Reader.read_as_text(val);		
		var lines = content.trim().split("\n").map(l => l.trim());

        // (0,0,0)=100 2 1
        // (0,0,1)=0.567 0 1
        // ...
		for (var i = 0; i < lines.length; i++) {
			var split = lines[i].split('=').map(l => l.trim()); 

			if (split.length != 2) continue;

			var coord = split[0].substring(1, split[0].length - 1).split(",").map(s => s.trim());
			var value = split[1].split(" ").map(v => v.trim());

			if (coord.length  == 2) coord.push("0");

			f0.add_state_message(new MessageStateCA(coord, value));
		}
	}


	/**                              
	 * Parses the *.map file. Adds corresponding messages to the frame 0.
	 * @param {Frame[]} frames - the simulation frames
	 * @param {string} t0  - the time 0 (i.e. 00:00:000)
	 * @param {Model} model - the top model of the simulation 
	 * @param {File} map - the *.map file
	 */	
	async read_map(frames, t0, model, map) {
		if (!map) return;

		var f0 = frames.get(t0) || frames.add(new Frame(t0));
		var content = await Reader.read_as_text(map);
		var lines = content.trim().split("\n").map(l => l.trim());		
		var i = 0;

		for (var x = 0; x < model.dim[0]; x++) {
			for (var y = 0; y < model.dim[1]; y++) {
				for (var z = 0; z < model.dim[2]; z++) {
					if (i == lines.length) throw new Error("missing initial values in map file."); 

					f0.add_state_message(new MessageStateCA([x, y, z], lines[i++].trim()));
				}
			}
		}
	}

	/**                              
	 * Parses the *val, *map and *.log file
	 * @param {Structure} structure - the structure object for a simulation
	 * @param {File} log - the *.log file
	 * @param {File} val - the *.val file
	 * @param {File} map - the *.map file
	 * @return {Frame[]} an array of frames built from the messages.log
	 */	
	async parse_messages(structure, log, val, map) {
		var lopez_test = await log.slice(0, 5).text();
		var is_lopez = lopez_test == "0 / L";
		var t0 = is_lopez ? "00:00:00:000:0" : "00:00:00:000";

		var model = structure.model_types[1];
		var frames = new List(f => f.time);

		if (!is_lopez) this.read_initial_value(frames, t0, model);
		if (!is_lopez) this.read_initial_row_values(frames, t0, model);

		await this.read_val(frames, t0, val);
		await this.read_map(frames, t0, model, map);

		await Reader.read_by_chunk(log, "\n", (parsed, chunk, progress) => {			
			var lines = chunk.split("\n");

			for (var i = 0; i < lines.length; i++) {
				var line = lines[i];

				// Mensaje Y / 00:00:00:100 / flu(18,12)(645) / out /      2.00000 para flu(02)
				// Mensaje Y / 00:00:20:000 / sender(02) / dataout /     11.00000 para top(01)
				if (line.startsWith("Mensaje Y")) this.parse_cdpp_line(frames, line);

				else if (line.startsWith("0 / L / Y")) this.parse_lopez_line(frames, model, line);
			}

			this.emit("progress", { progress: progress });
		});

		frames.forEach(f => {
			f.state_messages.forEach(m => {
				m.value = model.apply_template(m.value);
			});
		});

		return frames;
	}

	/**                              
	 * Parses a CDpp line from the *.log. Adds it to the frame provided.
	 * @param {Frame[]} frames - a time frame for the simulation
	 * @param {string} line - a line from the *.log
	 */	
	parse_cdpp_line(frames, line) {
		var split = line.split('/').map(l => l.trim());
		var model = split[2].replaceAll('(', ' ').replaceAll(')', '').split(' ')
		var coord = model.length == 3 ? model[1].split(',') : null;

		var time = split[1]
		var port = split[3]
		var value = split[4].split(' ')[0]

		var frame = frames.get(time) || frames.add(new Frame(time));

		if (coord) {
			if (coord.length == 2) coord.push("0");

			frame.add_state_message(new MessageStateCA(coord, [value]));
		}
	}

	/**                              
	 * Parses a Lopez line from the *.log. Adds it to the frame provided.
	 * @param {Frame[]} frames - a time frame for the simulation
	 * @param {TypeModel} model_type - the TypeModel of the top model
	 * @param {string} line - a line from the *.log
	 */	
	parse_lopez_line(frames, model_type, line) {
		var split = line.split('/').map(l => l.trim());
		var model = split[4].replaceAll('(', ' ').replaceAll(')', '').split(' ')
		var c = model.length == 3 ? model[1].split(',') : null;

		var time = split[3]
		var port = split[5]
		var value = split[6]

		var frame = frames.get(time) || frames.add(new Frame(time));

		if (c) {
			if (c.length == 2) c.push("0");

			var m = frame.state_messages.find(m => m.x == c[0] && m.y == c[1] && m.z == c[2])

			if (!m) {
				var values = model_type.template.map(t => "");

				m = frame.add_state_message(new MessageStateCA(c, values));
			}

			var idx = model_type.ports.findIndex(p => p.name == port);

			m.value[idx] = value;
		}
	}
}

'use strict';

import Core from '../tools/core.js';

/** 
 * A component used to record a simulation trace (diagram, grid or GIS)
 **/
export default class Recorder { 

	/**
	 * Gets true if the recorder is recording, false otherwise
	 * @type {boolean}
	 */
	get recording() { return this._recording; }

	/**
	 * Sets whether the recorder is recording
	 * @type {boolean}
	 */
	set recording(value) { this._recording = value; }

	/**
	 * @param {canvas} canvas - an HTML5 canvas element
	 */	
	constructor(canvas) {		
		this.canvas = canvas;
		this.chunks = null;
		this._recording = false;

		var options;

		if (MediaRecorder.isTypeSupported('video/webm;codecs=vp9')) {
			options = { mimeType: 'video/webm; codecs=vp9' };
		} 
		else {
			options = { mimeType: 'video/webm; codecs=vp8' };
		} 


		this.recorder = new MediaRecorder(canvas.captureStream(), options); // init the recorder

		// every time the recorder has new data, we will store it in our array
		this.recorder.ondataavailable = (function(ev) {
			if (ev.data && ev.data.size == 0) return;

			this.chunks.push(ev.data);
		}).bind(this);
	}	

	/**
	 * Begins the recording process
	 */	
	start() {
		this.recording = true;

		this.chunks = [];

		this.recorder.start();
	}

	/**
	 * Stops the recording process
	 */	
	stop() {
		this.recording = false;

		var d = Core.defer();

		this.recorder.onstop = e => d.Resolve();

		this.recorder.stop();

		return d.promise;
	}

	/**
	 * Downloads the video from the recorder
	 * @param {string} name - the name of the file to download
	 */	
	download(name) {
		// TODO : check if can use net.download
		if (this.chunks.length == 0) return;

		var blob = new Blob(this.chunks, { type: 'video/webm' });
		var url = URL.createObjectURL(blob);
		var a = document.createElement('a');

		document.body.appendChild(a);

		a.style = 'display: none';
		a.href = url;
		a.download = name + '.webm';
		a.click();

		window.URL.revokeObjectURL(url);
	}
}

import StaticFill from "./staticFill.js";

/** 
 * A fill style that varies according to the value of the geometry it is applied to
 **/
export default class BucketFill {

	/** 
	* Gets number of buckets for this style
	* @type {number} 
	*/
	get length() { return this.colors.length; }

    /**
     * @param {string} property - the property to use to apply classification
     * @param {string[]} colors - a list of string RGBA colors, same length as width
     * @param {string} type - the type of buckets (equivalent or quantile)
     * @param {number[]} buckets - a list of upper limits to use for classification
     * @param {number} zero - the colors to use for 0 value
     */
	constructor(property, colors, type, buckets, zero) {
		this.type = type;
		this.attribute = "fill";
		this.property = property;
		this.colors = colors;
		this.buckets = buckets || null;
		this.zero = zero || null;
	}

    /**
     * Build an OpenLayers style from this object
	 * @param {number} value - the value used to determine the style
     * @return {ol.style.Fill} an OL style for polygons
     */
	symbol(value) {
		var v = value[this.property];

		if (v == 0 && this.zero) return new ol.style.Fill({ color: this.zero });

		for (var i = 0; v > this.buckets[i] && i <= this.buckets.length; i++);

		return new ol.style.Fill({ color: this.colors[i] });
	}

    /**
     * Build from a json object
     * @return {BucketFill} the object built from json
     */
	static from_json(json) {
		return new BucketFill(json.property, json.colors, json.type, json.buckets, json.zero);
	}

    /**
     * Builds a default style for a fill
     * @return {StaticFill} a default style
     */
	static default_fill() {
		return new StaticFill('rgba(50,100,200,0.6)');
	}
}

