'use strict';

/** 
 * The Cache object contains an array of states of the simulation at a specified interval. 
 * It is used to navigate the simulation trace more efficiently with the playback bar.
 **/
export default class Cache { 

	/** 
	* Gets the number of cached states.
	* @type {number} 
	*/
	get length() { return this.states.length; }

	/** 
	* Gets interval between cached states.
	* @type {number} 
	*/
	get interval() { return this._interval; }

	/** 
	* Sets interval between cached states.
	* @type {number} 
	*/
	set interval(value) { this._interval = value; }

	/** 
	* Gets an array of states containing the cached states.
	* @type {State[]} 
	*/
	get states() { return this._states; }

	/** 
	* Gets an array of states containing the cached states.
	* @type {State[]} 
	*/
	set states(value) { this._states = value; }

    /**
     * @param {number} interval - The interval between cached states (1 cached state out of interval value).
     * @param {Frame[]} frames - An array of frame containing all the frames in the simulation.
     * @param {State} zero - An empty state object, valid for the simulation structure.
     */
	constructor(interval, frames, zero) {
		this.interval = interval;

		this.states = [];

		var state = zero.clone();

		for (var i = 0; i < frames.length; i++) {
			state.forward(frames[i]);

			if (i % interval === 0) this.add_state(state);
		}

		if (i % interval != 0) this.add_state(state);
	}

    /**
     * Return the cached state closest to the position of the frame indicated by index.
	 * @param {number} index - position of the state to find the closest cached state.
     * @return {State} the closest state to the position of the frame indicated by index.
     */
	get_closest(index) {
		var diff = index % this.interval;

		return this.get_state((index - diff) / this.interval);
	}

    /**
     * Adds a cached state to the cache.
	 * @param {State} state - the state to add to the cache.
     */
	add_state(state) {
		this.states.push(state.clone());
	}

    /**
     * Return the cached state at position index from the cache.
	 * @param {number} index - position of the cached state to find
     * @return {State} the cached state at position index.
     */
	get_state(index) {
		return this.states[index].clone();
	}


    /**
     * Return the first cached state.
     * @return {State} the first cached state.
     */
	first() {
		return this.get_state(0);
	}

    /**
     * Return the last cached state.
     * @return {State} the last cached state.
     */
	last() {
		return this.get_state(this.length - 1);
	}
}

'use strict';

/** 
 * The Frame object contains output and state messages for a given frame. 
 * Frames are added or removed from the simulation state to move forward or 
 * backward respectively.
 **/
export default class Frame { 

	/** 
	* Gets all messages for the frame.
	* @type {Message[]} 
	*/
	get messages() { return this._messages; }

	/** 
	* Gets output messages for the frame.
	* @type {Message[]} 
	*/
	get output_messages() { return this.messages.output; }

	/** 
	* Gets state messages for the frame.
	* @type {Message[]} 
	*/
	get state_messages() { return this.messages.state; }

	/** 
	* Gets the time representation for the frame.
	* @type {string} 
	*/
	get time() { return this._time; }

    /**
     * @param {string} time - The time representation for the frame.
     */
	constructor(time) {
		this._time = time;

		this._messages = {
			output : [],
			state : []
		}
	}

    /**
     * Add a message of the specified type to the frame .
	 * @param {Message} message - message to add.
	 * @param {string} type - the type of message to add (output or state).
     * @return {Message} the added message.
     */
	add_message(message, type) {
		this.messages[type].push(message);

		return message;
	}

    /**
     * Add an output message to the frame .
	 * @param {Message} message - message to add.
     * @return {Message} the added message.
     */
	add_output_message(message) {
		return this.add_message(message, "output");
	}

    /**
     * Add an state message to the frame .
	 * @param {Message} message - message to add.
     * @return {Message} the added message.
     */
	add_state_message(message) {
		return this.add_message(message, "state");
	}

    /**
     * Get the reverse of the frame object. A reverse frame is obtained by reversing
	 * all the messages in a frame. A reverse message has the values required to move a 
	 * model's state back to the preceding frame. 
	 * @return {Frame} the reversed message.
     */
	reverse () {
		var reverse = new Frame(this.time);

		for (var i = 0; i < this.state_messages.length; i++) {
			var m = this.state_messages[i];

			reverse.add_state_message(m.reverse());
		}

		return reverse;
	}

    /**
     * This function computes the differences between the state messages of this frame and 
	 * the state provided. Difference values are assigned to each message object
	 * @see Message, Model
     */
	difference(state) {
		for (var i = 0; i < this.state_messages.length; i++) {
			var m = this.state_messages[i];			
			var v = state.get_value(m.model);

			m.difference(v);
		}
	}
}

'use strict';

/** 
 * The message class contains the data that a model outputs or the state of the model.
 **/
export default class Message { 

	/** 
	* Gets the message value
	* @type {object} 
	*/
	get value() { return this._value; }

	/** 
	* Sets the message value
	* @type {object} 
	*/
	set value(value) { this._value = value; }

	/** 
	* Gets the message value's difference with the previous state. The diff value is used to move the simulation backwards.
	* @type {object} 
	*/
	get diff() { return this._diff; }

	/** 
	* Sets the message value's difference with the previous state. The diff value is used to move the simulation backwards.
	* @type {object} 
	*/
	set diff(value) { this._diff = value; }

    /**
     * @param {object} value - the message value.
     * @param {string} diff - the message value's difference with the previous state 
     */
	constructor(value, diff) {
		this.value = value != undefined ? value : null;
		this.diff = diff != undefined ? diff : null;
	}

    /**
     * Stores the value provided on this message. The provided value is usually the value of the 
	 * previous state for the model targeted by the message. The diff value is used subsequently 
	 * to return to the previous state when playing backwards. 
     */
	difference(v) {
		if (v === undefined || v === null) return;

		this.diff = {};

		for (var f in this.value) this.diff[f] = v[f];
	}

    /**
     * Reverses the message. Used to move backwards in the simulation
     */
	reverse() {		
		throw new Error("Reverse must be implemented by child class.");
	}
}

