'use strict';

let _nls = {};
let _locale = document.documentElement.lang || "en";
let _templatables = {}

/** 
 * A utility class that contains a series of basic static functions
 **/
export default class Core {
	
	/**
	* Gets the current locale
	* @type {string} 
	*/
    static get locale() { return _locale; }
	
	/**
	* Sets the current locale
	* @type {string} 
	*/
    static set locale(value) { _locale = value; }

	/**
	* Get or set a templated class definition, this is required to nest Templated UI 
	* components within other Templated UI components.
	* @param {string} id - the id of the templated class definition to get or set
	* @param {class} definition - when specified, the class definition to set 
	* @return {class} if definition was not provided, the class definition corresponding to the id.
	*/
	static templatable(id, definition) {
		if (definition) {
			if (_templatables[id]) throw new Error(`Templatable ${id} is defined multiple times.`);
			
			else _templatables[id] = definition;
		}
		
		else if (!_templatables[id]) throw new Error(`Templatable ${id} is not defined.`);
		
		return _templatables[id];
	}
	
	/**
	* A convenience function to get a deffered object for asynchronous processing. 
	* Removes one level of nesting when working with promises
	* @return {object} an object that contains a promise object, a resolve and reject function
	*/
	static defer() {
		var defer = {};
		
		defer.promise = new Promise((resolve, reject) => {
			defer.Resolve = (result) => { resolve(result); };
			defer.Reject = (error) => { reject(error); };
		});
		
		defer.Resolved = (value) => {
			defer.Resolve(value);
			
			return defer.promise;
		}
		
		defer.Rejected = (error) => {
			defer.Reject(error);
			
			return defer.promise;
		}
		
		return defer;
	}
	
	/**
	* Merges an object into another object. 
	* @param {object} a - the object that will receive the properties 
	* @param {object} b - the object to merge into object A
	* @return {object} the modified Object
	*/
	static mixin(a, b) {				
		for (var key in b) {
			if (b.hasOwnProperty(key)) a[key] = b[key];
		}

		// TODO : Why did I use arguments[0] instead of a?
		return arguments[0];
	}
	
	/**
	* Returns a promise that waits until the HTML document is loaded. 
	* @return {promise} a promise, resolved when the HTML document is loaded.
	*/
	static wait_for_document() {
		var d = Core.defer();
		
		if (document.readyState === "complete") d.Resolve();
		
		else window.addEventListener('load', (ev) => d.Resolve());
		
		return d.promise;
	}
}

'use strict';

import Core from './core.js';

/** 
 * A utility class that contains a series of basic functions to manipulate the DOM tree
 **/
export default class Dom {

	/**
	* Create an element
	* @param {string} tagName - The type of element to be created (div, span, label, input, etc.)
	* @param {object} options - A dictionary type object containing the options to assign to the created Element
	* @param {element} pNode - The parent element where the created element will be apended
	* @returns {element} The element created
	*/
	static create(tagName, options, pNode) {
		var elem = document.createElement(tagName);

		Core.mixin(elem, options);

		this.place(elem, pNode);

		return elem
	}

	/**
	* Append an element to another element
	* @param {element} elem - The element to be appended
	* @param {element} pNode - The parent element where the element will be appended
	*/
	static place(elem, pNode) {
		if (!!pNode) pNode.appendChild(elem);
	}

	/**
	* Remove all children of an element
	* @param {element} elem - The element to empty
	*/
	static empty(elem) {
		while (elem.firstChild) {
			elem.removeChild(elem.firstChild);
		}
	}

	/**
	* Add a CSS rule to an element
	* @param {element} elem - The element to modify
	* @param {string} css - The CSS rule to add to the element
	*/
	static add_css(elem, css) {
		var c1 = elem.className.split(" ");

		css.split(" ").forEach(function(c) {
			if (c1.indexOf(c) == -1) c1.push(c);
		})

		elem.className = c1.join(" "); 
	}

	/**
	* Remove a CSS rule from an element
	* @param {element} elem - The element to modify
	* @param {string} css - The CSS rule to remove from the element
	*/
	static remove_css(elem, css) {				
		var c1 = elem.className.split(" ");
		var c2 = css.split(" ");

		elem.className = c1.filter(function(c) { return c2.indexOf(c) == -1; }).join(" ");
	}

	/**
	* Set the CSS rules on an element
	* @param {element} elem - The element to modify
	* @param {string} css - The CSS rule to set on the element
	*/
	static set_css(elem, css) {
		elem.className = css; 
	}

	/**
	* Toggle a CSS rule on or or off for an element
	* @param {element} elem - The element to modify
	* @param {string} css - The CSS rule to toggle on the element
	* @param {boolean} enabled - True to toggle the CSS rule on, false to toggle it off
	*/
	static toggle_css(elem, css, enabled) {
		if (enabled) this.add_css(elem, css);

		else this.remove_css(elem, css);
	}

	/**
	* Get an attribute value from an element
	* @param {element} elem - The element to retrieve the attribute from
	* @param {string} attr - The name of the attribute to retrieve
	* @returns {string} The value of the attribute if found, null otherwise
	*/
	static get_attribute(elem, attr) {
		var attr = elem.attributes.getNamedItem(attr);

		return attr ? attr.value : null;
	}

	/**
	* Returns the geometry of a dom node (width, height)
	* @param {element} elem - The element from which to retrieve the geometry
	* @returns {object} An object containing the unpadded width and height of the element
	*/
	static geometry(elem) {
		var style = window.getComputedStyle(elem);

		var h = +(style.getPropertyValue("height").slice(0, -2));
		var w = +(style.getPropertyValue("width").slice(0, -2));
		var pL = +(style.getPropertyValue("padding-left").slice(0, -2));
		var pR = +(style.getPropertyValue("padding-right").slice(0, -2));
		var pT = +(style.getPropertyValue("padding-top").slice(0, -2));
		var pB = +(style.getPropertyValue("padding-bottom").slice(0, -2));

		var w = w - pL - pR;
		var h = h - pT - pB;

		return { w : w , h : h }
	}
}

import Core from './core.js';

/** 
 * A utility class that contains a series of basic functions for requests
 **/
export default class Net {

	/**
	* builds a url string from a query object and base url
	* @param {string} base - the base URL
	* @param {object} query - a key, value map that will be converted to URL parameters
	* @return {string} the complete URL.
	*/
	static url(base, query) {
		var keys = Object.keys(query);

		var params = keys.length > 0 ? keys.map(k => `${k}=${query[k]}`).join("&") : null;

		return params ? `${base}?${params}` : base;
	}

	/**
	* Execute a web request using the fetch native API
	* @param {string} url - the URL to the file to read
	* @param {object} options - options to pass to the fetch request
	* @param {boolean} optional - indicates whether the file is optional or not. If optional, a 404
	* 							  error will not throw an error.
	* @return {promise} a promise resolved when the request is complete.
	*/
	static fetch(url, options, optional){
		var d = Core.defer();
		var p = fetch(url, options);

		p.then((response) => {
			if (response.status == 200) d.Resolve(response);

			else if (response.status > 399 && response.status < 500 && !!optional) d.Resolve(null);

			else d.Reject(new Error(`Url ${url} returned ${response.status} ${response.statusText}`));
		}, (error) => d.Reject(error));

		return d.promise;
	}

	/**
	* reads a blob object from a URL
	* @param {string} url - the URL to the file to read
	* @param {object} options - options to pass to the fetch request
	* @param {boolean} optional - indicates whether the file is optional or not. If optional, a 404
	* 							  error will not throw an error.
	* @return {promise} a promise resolved when the blob is retrieved.
	*/
	static fetch_blob(url, options, optional) {
		var d = Core.defer();

		this.fetch(url, options, optional).then(response => {
			if (response == null) d.Resolve(null);

			else response.blob().then(blob => d.Resolve(blob), error => d.Reject(error));
		}, error => d.Reject(error));

		return d.promise;
	}

	/**
	* reads a text from a URL
	* @param {string} url - the URL to the file to read
	* @param {object} options - options to pass to the fetch request
	* @param {boolean} optional - indicates whether the file is optional or not. If optional, a 404
	* 							  error will not throw an error.
	* @return {promise} a promise resolved when the text is retrieved.
	*/
	static fetch_text(url, options, optional) {
		var d = Core.defer();

		this.fetch(url, options, optional).then(response => {
			if (response == null) d.Resolve(null);

			else response.text().then(text => d.Resolve(text), error => d.Reject(error));
		}, error => d.Reject(error));

		return d.promise;
	}		

	/**
	* reads a json object from a URL
	* @param {string} url - the URL to the file to read
	* @param {object} options - options to pass to the fetch request
	* @param {boolean} optional - indicates whether the file is optional or not. If optional, a 404
	* 							  error will not throw an error.
	* @return {promise} a promise resolved when the json is retrieved.
	*/
	static json(url, options, optional) {
		var d = Core.defer();

		this.fetch(url, options, optional).then(response => { 
			if (response == null) d.Resolve(null);

			else response.json().then(json => d.Resolve(json), error => d.Reject(error));
		}, error => d.Reject(error));

		return d.promise;
	}

	/**
	* reads a remote file object from a URL
	* @param {string} url - the URL to the file to read
	* @param {string} name - the name of the file to read
	* @param {boolean} optional - indicates whether the file is optional or not. If optional, a 404
	* 							  error will not throw an error.
	* @return {promise} a promise resolved when the file is retrieved.
	*/
	static file(url, name, optional) {
		var d = Core.defer();

		Net.fetch_blob(url, null, optional).then(b => {			
			d.Resolve(b ? new File([b], name) : null);
		}, error => d.Reject(error));

		return d.promise;
	}

	/**
	* Get a parameter value from the document URL
	* @param {string} name - the name of the parameter to retrieve from the URL
	* @return {string} the value of the parameter from the URL, an empty string if not found
	*/
	static get_url_parameter (name) {				
		name = name.replace(/[\[\]]/g, '\\$&');

		var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)');

		var results = regex.exec(window.location.href);

		if (!results) return null;

		if (!results[2]) return '';

		return decodeURIComponent(results[2].replace(/\+/g, ' '));
	}

	/**
	* Download content as a file
	* @param {string} name - the name of the file to download
	* @param {string} content -	the content of the file to save
	*/
	static download(name, content) {
		var link = document.createElement("a");

		// link.href = "data:application/octet-stream," + encodeURIComponent(content);
		link.href = URL.createObjectURL(content);
		link.download = name;
		link.click();
		link = null;
	}

	/**
	* Gets the base URL for the app
	* @return {string} the base path to the web app
	*/
	static app_path() {
		var path = location.href.split("/");

		path.pop();

		return path.join("/");
	}

	/**
	* Gets the base URL for the app
	* @return {string} the base path to the file
	*/
	static file_path(file) {
		file = file.charAt(0) == "/" ? file.substr(1) : file;

		var path = [Net.app_path(), file];

		return path.join("/");
	}
}

import BucketFill from "../components/style/bucketFill.js";
import BucketRadius from "../components/style/bucketRadius.js";
import BucketStroke from "../components/style/bucketStroke.js";
import BucketScale from "../components/style/bucketScale.js";
import StaticFill from "../components/style/staticFill.js";
import StaticRadius from "../components/style/staticRadius.js";
import StaticStroke from "../components/style/staticStroke.js";
import StaticScale from "../components/style/staticScale.js";
import Polygon from "../components/style/polygon.js";
import Point from "../components/style/point.js";
import PointIcon from "../components/style/pointIcon.js";
import Linestring from "../components/style/linestring.js";

/** 
 * A utility class that contains a series of basic functions for styling maps
 **/
export default class Style {

	/**
	* calculates a series of statistics useful for map classification (min, max, sorted values, etc.)
	* @param {Simulation} simulation - the simulation object
	* @return {object} the statistics calculated from the simulation.
	*/
	static statistics(simulation) {		
		var values = {};

		// TODO :Something doesn't work here now that there can be multiple model types. 
		// Statistics should be computed in the GIS part of the app, by property on the map.
		for (var i = 0; i < simulation.frames.length; i++) {
			var fr = simulation.frames[i];

			for (var j = 0; j < fr.state_messages.length; j++) {
				var m = fr.state_messages[j];

				for (var f in m.value) {
					if (!values[f]) values[f] = [];

					values[f].push(m.value[f]);
				}
			}
		}

		for (var f in values) {
			values[f] = values[f].filter(v => !isNaN(v));
			values[f] = values[f].sort((a, b) => (a < b) ? -1 : 1);
		} 

		var stats = {};

		for (var f in values) {
			var length = values[f].length;

			stats[f] = {
				sorted: values[f],
				length: length,
				min: values[f][0],
				max: values[f][length - 1]
			}
		}

		return stats;
	}

	/**
	* calculates a classification buckets using quantiles
	* @param {number[]} values - an array of numbers, all the values in the simulation
	* @param {number} n - the number of buckets
	* @param {boolean} zero - if true, filter out 0 values.
	* @return {number[]} an array of numbers representing upper limits of buckets.
	*/
	static quantile_buckets(values, n, zero) {
		var buckets = [];

		var zValues = zero ?  values.filter(v => v != 0) : values;

		if (zValues.length == 0) return [0];

		var interval = Math.floor(zValues.length / n);

		for (var i = 1; i < n; i++) buckets.push(zValues[i * interval]);

		buckets.push(zValues[zValues.length - 1]);

		return buckets;
	}

	/**
	* calculates a classification buckets using equivalent ranges
	* @param {number} min - the minimum value among all simulation values
	* @param {number} max - the maximum value among all simulation values
	* @param {number} n - the number of buckets.
	* @return {number[]} an array of numbers representing upper limits of buckets.
	*/
	static equivalent_buckets(min, max, n) {
		var buckets = [];

		var interval = (max - min) / n;

		for (var i = 1; i < n; i++) buckets.push(min + i * interval);

		buckets.push(max);

		return buckets;
	}

	/**
	* Apply buckets to a style definition. Adds a buckets property on the style definition.
	* @param {object} style - the style definition
	* @param {object} stats - the simulation statistics
	*/
	static bucketize_style(style, stats) {
		if (style.type == "quantile") {	
			style.buckets = Style.quantile_buckets(stats[style.property].sorted, style.length, !!style.zero);
		}
		else if (style.type == "equivalent") {
			style.buckets = Style.equivalent_buckets(stats[style.property].min, stats[style.property].max, style.length);
		}
	}

	/**
	* Get an OpenLayers style object from a type and a style definition
	* @param {string} type - the type of style (point, pointIcon, polygon, linestring)
	* @param {object} json - the style definition
	* @return an ol.style.Style object
	*/
	static get_style(type, json) {
		if (type == "point") return this.point_style(json);

		if (type == "pointIcon") return this.point_icon_style(json);

		if (type == "polygon") return this.polygon_style(json);

		if (type == "linestring") return this.linestring_style(json);
	}

	/**
	* Get an OpenLayers point icon style from a style definition
	* @param {object} json - the style definition
	* @return an ol.style.Style object using an ol.style.Icon
	*/
	static point_icon_style(json) {
		return new ol.style.Style({
			image: new ol.style.Icon({
				anchor: [0.5, 0.5],
				anchorXUnits: 'fraction',
				anchorYUnits: 'fraction',
				scale: json.scale,
				offset: [0,0],
				opacity: 1,
				src: json.src
			})
		});
	}

	/**
	* Get an OpenLayers point style from a style definition
	* @param {object} json - the style definition
	* @return an ol.style.Style object using an ol.style.Circle
	*/
	static point_style(json) {
		return new ol.style.Style({
			image: new ol.style.Circle({
				radius: json.radius,
				fill: new ol.style.Fill({
					color: json.fill.color
				}),
				stroke: new ol.style.Stroke({
					color: json.stroke.color,
					width: json.stroke.width,
				})
			})
		});
	}

	/**
	* Get an OpenLayers polygon style from a style definition
	* @param {object} json - the style definition
	* @return an ol.style.Style object using an ol.style.Stroke and ol.style.Fill
	*/
	static polygon_style(json) {
		return new ol.style.Style({
			stroke: new ol.style.Stroke({
				color: json.stroke.color,
				width: json.stroke.width,
			}),
			fill: new ol.style.Fill({
				color: json.fill.color,
			})
		});
	}

	/**
	* Get an OpenLayers linestring style from a style definition
	* @param {object} json - the style definition
	* @return an ol.style.Style object using an ol.style.Stroke
	*/
	static linestring_style(json) {
		return new ol.style.Style({
			stroke: new ol.style.Stroke({
				color: json.stroke.color,
				width: json.stroke.width
			})
		})
	}

	/**
	* Get a fill style from visual variable definition
	* @param {object} json - the visual variable definition
	* @return {StaticFill, BucketFill} the style object created
	*/
	static fill_style_from_json(json) {
		if (json.type == "equivalent" || json.type == "quantile" || json.type == "user-defined") {
			return BucketFill.from_json(json);
		}

		if (json.type == "static") return StaticFill.from_json(json);
	}

	/**
	* Get a stroke style from visual variable definition
	* @param {object} json - the visual variable definition
	* @return {StaticStroke, BucketStroke} the style object created
	*/
	static stroke_style_from_json(json) {
		if (json.type == "equivalent" || json.type == "quantile" || json.type == "user-defined") {
			return BucketStroke.from_json(json);
		}

		if (json.type == "static") return StaticStroke.from_json(json);
	}

	/**
	* Get a radius style from visual variable definition
	* @param {object} json - the visual variable definition
	* @return {StaticRadius, BucketRadius} the style object created
	*/
	static radius_style_from_json(json) {
		if (json.type == "equivalent" || json.type == "quantile" || json.type == "user-defined") {
			return BucketRadius.from_json(json);
		}

		if (json.type == "static") return StaticRadius.from_json(json.radius);	
	}

	/**
	* Get a scale style from visual variable definition
	* @param {object} json - the visual variable definition
	* @return {StaticScale, BucketScale} the style object created
	*/
	static scale_style_from_json(json) {
		if (json.type == "equivalent" || json.type == "quantile" || json.type == "user-defined") {
			return BucketScale.from_json(json);
		}

		if (json.type == "static") return StaticScale.from_json(json.scale);	
	}

	/**
	* Get a style for a geometry type and visual variable definition
	* @param {string} type - the gepometry type
	* @param {object} json - the visual variable definition
	* @return a style for the geometry initialized from the type and visual variable definition
	*/
	static from_json(type, json) {
		if (type == "polygon") return Polygon.from_json(json);

		if (type == "point") {
			if (json.radius) return Point.from_json(json);

			if (json.src) return PointIcon.from_json(json);
		}

		if (type == "linestring") return Linestring.from_json(json);
	}
}

