/** 
 * A color picker element
 **/
export default class Picker extends Widget {

	/**
	 * Get the current color from the picker
	 * @type {Color}
	 */
	get color() {
		return this.picker.color;
	}

	/**
	 * Get the current color (RGB) from the picker
	 * @type {number[]}
	 */
	get color3() {
		var c = this.color.rgb;

		return [c.r, c.g, c.b];
	}

	/**
	 * Get the current color (RGBA) from the picker
	 * @type {number[]}
	 */
	get color4() {
		var c = this.color.rgba;

		return [c.r, c.g, c.b, c.a * 255];
	}

	/**
	 * Set the current color on the picker
	 * @type {Color}
	 */
	set color(value) {
		this.picker.color.set(value);

		this.elems.button.style.backgroundColor = this.picker.color.rgbString;
	}

	/**
	 * Constructor for the Picker element. Follows the widget creation pattern.
	 * @param {object} container - div container
	 */	
	constructor(container) {
		super(container);

		this.h = null;
		this.collapsed = true;

		this.elems.button.addEventListener("click", this.on_button_color_click.bind(this));
		this.elems.container.addEventListener("click", this.on_container_click.bind(this));

		this.picker = new iro.ColorPicker(this.elems.wheel, {
			width : 170,
			layoutDirection : "vertical",
			sliderSize : 15
		});

		this.picker.base.children[0].tabIndex = 0;
		this.picker.base.children[1].tabIndex = 0;

		this.elems.button.style.backgroundColor = this.picker.color.rgbString;

		this.on_body_keyup_bound = this.on_body_keyup.bind(this);
		this.on_body_click_bound = this.on_body_click.bind(this);
	}

	/**
	 * Handles the click on the container of the picker
	 * Prevents propagation
	 * @param {event} ev - event object
	 */
	on_container_click(ev) {
		ev.stopPropagation();
	}

	/**
	 * Handles the click event on the color button
	 * Shows the picker if collapsed, hides it otherwise
	 * @param {event} ev - event object
	 */
	on_button_color_click(ev) {
		this.collapsed ? this.show() : this.hide();
	}

	/**
	 * Shows the picker
	 */
	show() {		
		document.body.addEventListener("keyup", this.on_body_keyup_bound);
		document.body.addEventListener("click", this.on_body_click_bound);

		this.collapsed = false;

		Dom.remove_css(this.elems.container, "collapsed");
	}

	/**
	 * Hides the picker
	 */
	hide() {
		document.body.removeEventListener("keyup", this.on_body_keyup_bound);
		document.body.removeEventListener("click", this.on_body_click_bound);

		this.collapsed = true;

		Dom.add_css(this.elems.container, "collapsed");

		this.elems.button.style.backgroundColor = this.picker.color.rgbString;

		this.emit("change", { color:this.picker.color.rgba });
	}

	/**
	 * Handles the keyup event on the picker element
	 * Hides the picker if Esc key
	 * @param {event} ev - event object
	 */
	on_body_keyup(ev) {
		if (ev.keyCode == 27) this.hide();
	}

	/**
	 * Handles the click event on the picker element
	 * Hides the picker unless clicking on the button
	 * @param {event} ev - event object
	 */
	on_body_click(ev) {
		if (this.elems.button == ev.target) return;

		this.hide();
	}

	/**
	 * Create HTML for select element
	 * @returns {string} HTML for select element
	 */
	html() {
		return "<div class='color-picker'>" +
				  "<button handle='button' class='color'></button>" +
			      "<div handle='container' class='wheel-container collapsed'>" +
					  "<div handle='wheel'></div>" +
				  "</div>" +
			   "</div>"
	}
}

Core.templatable("Api.UI.Picker", Picker);

import Dom from '../tools/dom.js';
import Core from '../tools/core.js';
import Widget from '../base/widget.js';

/** 
 * A moveable popup box
 **/
export default class Popup extends Widget { 

	/**
	 * Set the header title for the popup
	 * @type {string}
	 */
	set title(value) { this.elems.title.innerHTML = value; }

	/**
	 * Set the content widget in the popup
	 * @type {Widget}
	 */
	set content(widget) {
		this.empty();

		this._widget = widget;

		widget.container = this.elems.body;
	}

	/**
	 * Get the content widget in the popup
	 * @type {Widget}
	 */
	get widget() { return this._widget; }

	/**
	 * Set the content widget in the popup. Same as the content setter.
	 * @type {Widget}
	 */
	set widget(value) { this.content = value; }

	/**
	 * Constructor for the Picker element. Follows the widget creation pattern.
	 * @param {object} container - div container
	 * @param {object} container - div container
	 * Adds it to the document body by default.
	 */	
	constructor(title, widget) {	
		super(document.body);

		if (title) this.title = title;
		if (widget) this.widget = widget;

		this.moving = false;
		this.offset = { x:0, y:0 };
		this.defer = null;
		this.h = null;

		this.on_body_keyup_bound = this.on_body_keyup.bind(this);

		this.elems.blocker = Dom.create("div", { className:"popup-blocker" }, document.body);

		this.set_style(0, "hidden", "none");

		this.elems.close.addEventListener("click", this.on_btn_close_click.bind(this));
		this.elems.blocker.addEventListener("click", this.on_blocker_click.bind(this));

		this.container.addEventListener("mousedown", this.on_popup_mouse_down.bind(this));
		this.container.addEventListener("mouseup", this.on_popup_mouse_up.bind(this));
		this.container.addEventListener("mousemove", this.on_popup_mouse_move.bind(this));
	}

	/**
	 * Set the popup style
	 * @param {number} opacity - the opacity value for the popup 
	 * @param {string} visibility - the visibility value for the popup 
	 * @param {string} display - the display value for the popup 
	 */
	set_style(opacity, visibility, display) {
		this.elems.blocker.style.opacity = opacity;
		this.elems.blocker.style.visibility = visibility;
		this.elems.blocker.style.display = display;

		this.elems.popup.style.opacity = opacity;
		this.elems.popup.style.visibility = visibility;
		this.elems.popup.style.display = display;
	}

	/**
	 * Empties the popup
	 */
	empty() {
		Dom.empty(this.elems.body);
	}

	/**
	 * Gets the x,y coordinates to center the popup in the window
	 * @return {object} an object containing the x,y coordinates to center the popup
	 */
	get_center() {
		var geo = Dom.geometry(this.elems.popup);

		return { 
			x : window.innerWidth / 2 - geo.w / 2,
			y : window.innerHeight / 2 - geo.h / 2
		}
	}

	/**
	 * Shows the popup in the center of the screen
	 */
	show() {	
		this.defer = Core.defer();
		this.h = document.body.addEventListener("keyup", this.on_body_keyup_bound);		

		this.set_style(1, "visible", "block");

		var center = this.get_center();

		this.move(center.x, center.y);
		this.emit("show", { popup:this });

		this.elems.close.focus();

		return this.defer.promise;
	}

	/**
	 * Hides the popup
	 */
	hide() {		
		document.body.removeEventListener("keyup", this.on_body_keyup_bound);

		this.set_style(0, "hidden", "none");

		this.emit("hide", { popup:this });

		this.defer.Resolve();
	}

	/**
	 * Handles the keyup event on the popup.
	 * Hides on esc keyCode
	 * @param {event} ev - event object
	 */
	on_body_keyup(ev) {
		if (ev.keyCode == 27) this.hide();
	}

	/**
	 * Handles the click event on the blocker div.
	 * Hides the popup
	 * @param {event} ev - event object
	 */
	on_blocker_click(ev) {
		this.hide();
	}

	/**
	 * Handles the click event on the close button.
	 * Hides the popup
	 * @param {event} ev - event object
	 */
	on_btn_close_click(ev) {
		this.emit("close", { popup:this });

		this.hide();
	}

	/**
	 * Handles the mouse down event on the popup
	 * Initiates the drag action
	 * @param {event} ev - event object
	 */
	on_popup_mouse_down(ev) {
		if (ev.target !== this.elems.title) return;

		var rect = this.elems.popup.getBoundingClientRect();

		// offset between clicked point and top left of popup
		this.offset.x = ev.clientX - rect.x;
		this.offset.y = ev.clientY - rect.y;

		this.moving = true;

		this.on_popup_mouse_move(ev);
	}

	/**
	 * Handles the mouse up event on the popup
	 * Stops the drag action
	 * @param {event} ev - event object
	 */
	on_popup_mouse_up(ev) {
		this.moving = false;
	}

	/**
	 * Handles the mouse move event on the popup
	 * Moves the popup when dragging
	 * @param {event} ev - event object
	 */
	on_popup_mouse_move(ev) {
		if (!this.moving) return;

		ev.preventDefault();

		var x = ev.pageX - this.offset.x;
		var y = ev.pageY - this.offset.y;

		this.move(x, y);
	}

	/**
	 * Move the popup to an X,Y position
	 * @param {number} x - the x coordinate
	 * @param {number} y - the y coordinate
	 */
    move(x, y) {
		this.elems.popup.style.transform = `translate3d(${x}px, ${y}px, 0)`;
    }

	/**
	 * Create HTML for select element
	 * @returns {string} HTML for select element
	 */
	html() {
		return "<div handle='popup' class='popup'>" +
				  "<div class='popup-header'>" +
					  "<h2 class='popup-title' handle='title'></h2>" +
					  "<button class='close' handle='close' title='nls(Popup_Close)'>×</button>" +
				  "</div>" +
				  "<div class='popup-body' handle='body'></div>" +
				  "<div class='popup-footer' handle='footer'></div>" +
			   "</div>";
	}

	/**
	 * Defines the localized strings used in this component
	 * @param {Nls} nls - The nls object containing the strings
	 */
	localize(nls) {
		super.localize(nls);

		nls.add("Popup_Close", "en", "Close");
		nls.add("Popup_Close", "fr", "Fermer");
	}
}

Core.templatable("Api.UI.Popup", Popup);

'use strict';

import Core from '../tools/core.js';
import Dom from '../tools/dom.js';
import Widget from '../base/widget.js';

/** 
 * A replacement for the select component
 **/
export default class Select extends Widget {

	/**
	 * Get select box value
	 * @type {number}
	 */
	get value() {
		return this.root.value;
	}

	/**
	 * Set select box value
	 * @type {number}
	 */
	set value(value) {
		this.root.value = value;
	}

	/**
	 * Get select box title
	 * @type {string}
	 */
	get title() { return this.root.title; }

	/**
	 * Set select box title
	 * @type {string}
	 */
	set title(value) { this.root.title = value; }

	/**
	 * Get disabled state of the tooltip
	 * @type {boolean}
	 */
	get disabled() { return this.root.disabled; }

	/**
	 * Set disabled state of the tooltip
	 * @type {boolean}
	 */
	set disabled(value) { this.root.disabled = value; }

	/**
	 * Get selected element object (label, value)
	 * @type {object}
	 */
	get selected() {
		var i = this.root.value;

		return this._items[i];
	}

	/**
	 * Set the placeholder value of select box
	 * @type {string}
	 */
	set placeholder(value) {
		this._ph = Dom.create("option", { innerHTML:value, value:-1, className:"select-placeholder" });

		this._ph.disabled = true;
		this._ph.selected = true;

		this.root.insertBefore(this._ph, this.root.firstChild);
	}

	/**
	 * Constructor for the select element. Follows the widget creation pattern.
	 * @param {object} container - div container
	 */	
	constructor(container) {
		super(container);

		this._items = [];

		this._ph = null;

		this.root.addEventListener("change", this.on_select_change.bind(this));
	}

	/**
	 * Populate options in select box
	 * @param {string} label - Label of select option
	 * @param {string} title - Title of select option
	 * @param {object} item - Object containing option description, label, and value
	 */
	add(label, title, item) {
		Dom.create("option", { innerHTML:label, value:this._items.length, title:title || "" }, this.root);
		this.root.querySelectorAll('option').forEach( x=> x.setAttribute("role", "option")); // for accessibility

		this._items.push(item);
	}

	/**
	 * This function selects the first value in the component for which the delegate function returns true.
	 * @param {function} delegate  - Delegate function
	 */
	select(delegate) {		
		this.value = this.find_index(delegate);
	}

	/**
	 * Finds index of selected classification method through link to styler widget
	 * @param {function} delegate - Delegate function
	 * @returns {number} Index number of selected classification method (-1 if none)
	 */
	find_index(delegate) {
		for (var i = 0; i < this._items.length; i++) {
			if (delegate(this._items[i], i)) return i;
		}

		return -1;
	}

	/**
	 * Emit change event when a new option is selected
	 * @param {object} ev - Event object
	 */
	on_select_change(ev) {
		var item = this._items[ev.target.value];

		this.emit("change", { index:ev.target.value, item:item, label:ev.target.innerHTML });
	}

	/**
	 * Create HTML for select element
	 * @returns {string} HTML for select element
	 */
	html() {
		return '<select handle="root" role="listbox"></select>'; // role for accessibility
	}

	/**
	 * Empty items in select box
	 */
	empty() {
		Dom.empty(this.root);

		this._items = [];

		if (!this._ph) return;

		Dom.place(this._ph, this.root);

		this._ph.selected = true;
