'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Tooltip from '../../ui/tooltip.js';
import Automator from '../../components/automator.js';
import Diagram from '../diagram/diagram.js';

export default Core.templatable("Api.Widget.Diagram.Auto", class AutoDiagram extends Automator { 

	constructor(node, simulation, options) {
		if (!options) throw new Error("No options provided for the Diagram widget");

		super(new Diagram(node), simulation);

		this.options = options;

		this.widget.set_diagram(this.simulation);

		this.widget.draw(this.simulation.current_frame.output_messages);

		this.selected = [];

		this.attach_handlers(options);

		this.update_selected();

		this.tooltip = new Tooltip();

		this.simulation.on("new-diagram", this.on_simulation_new_diagram.bind(this));
	}

	attach_handlers(options) {
		var h = [];

		if (options.hoverEnabled != false) h.push(this.widget.on("mousemove", this.on_mousemove.bind(this)));
		if (options.hoverEnabled != false) h.push(this.widget.on("mouseout", this.on_mouseout.bind(this)));
		if (options.clickEnabled != false) h.push(this.widget.on("click", this.on_click.bind(this)));

		h.push(this.simulation.on("move", this.on_simulation_change.bind(this)));
		h.push(this.simulation.on("jump", this.on_simulation_change.bind(this)));
		h.push(this.simulation.on("selected", this.on_simulation_change.bind(this)));

		options.on("change", this.on_settings_change.bind(this));

		this.handle(h);
	}

	update_selected() {
		this.selected = this.simulation.selected;
	}

	resize() {
		var size = this.options.diagram_size(this.simulation);

		this.widget.container.style.width = size.width + "px";
		this.widget.container.style.height = size.height + "px";	
	}

	redraw() {
		this.widget.resize();
	}

	on_simulation_new_diagram(ev) {
		this.widget.set_diagram(this.simulation);

		this.widget.draw(this.simulation.current_frame.output_messages);
	}

	on_settings_change(ev) {
		if (["height", "width", "aspect"].indexOf(ev.property) == -1) return;

		this.resize();
		this.redraw();
	}

	on_simulation_change(ev) {		
		var messages = this.simulation.current_frame.output_messages;

		this.widget.draw(messages);
	}

	on_mousemove(ev) {
		var messages = this.simulation.current_frame.output_messages;

		Dom.empty(this.tooltip.elems.content);

		var tY = messages.filter(t => t.model.id == ev.model.id);

		if (tY.length == 0) return;

		tY.forEach(t => {
			var value = JSON.stringify(t.value);
			var subs = [t.model.id, value, t.port.name];
			var html = this.nls("Diagram_Tooltip_Y", subs);

			Dom.create("div", { className:"tooltip-label", innerHTML:html }, this.tooltip.elems.content);
		});

		this.tooltip.show(ev.x + 20, ev.y);
	}

	on_click(ev) {
		var idx = this.selected.indexOf(ev.model);

		// TODO : Selection should be handled by diagram, not auto class
		if (idx == -1) {
			this.selected.push(ev.model);

			ev.svg.classList.add("selected");
		}
		else {
			this.selected.splice(idx, 1);

			ev.svg.classList.remove("selected");
		}
	}

	on_mouseout(ev) {
		this.tooltip.hide();
	}

	localize(nls) {
		super.localize(nls);

		nls.add("Diagram_Tooltip_Y", "en", "<b>{0}</b> emitted <b>{1}</b> through port <b>{2}</b>");
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Widget from '../../base/widget.js';

export default Core.templatable("Api.Widget.Diagram", class Diagram extends Widget { 

	get canvas() { return this.elems.canvas; }

	get svg() { return this.simulation.diagram; }

	constructor(container) {
		super(container);
	}

	set_diagram(simulation) {
		this.simulation = simulation;

		this._svg = { dests: {}, origins: {} };

		this.load_origin_svg_nodes();
		this.load_destination_svgs();

		Dom.empty(this.elems.diagram);

		this.elems.diagram.appendChild(this.svg);

		var style = "marker.highlighted path {fill: #1e94c3 !important;stroke: #1e94c3 !important;}marker.highlighted.origin path {fill: #b36402 !important;stroke: #b36402 !important;}text.highlighted {fill: #1e94c3 !important;}text.highlighted.origin {fill: #b36402 !important;}path.highlighted {stroke: #1e94c3 !important;}path.highlighted.origin {stroke: #b36402 !important;}.highlighted:not(text):not(path) {stroke: #1e94c3 !important;fill: #d6f2fd !important;}.highlighted.origin:not(text):not(path) {stroke: #b36402 !important;fill: #f9e5c1 !important;}";

		Dom.create("style", { innerHTML:style }, this.elems.diagram.querySelector("svg"));

		this.svg.setAttribute("preserveAspectRatio", "none");

		this.set_pointer_events();
	}

	set_pointer_events() {		
		this.svg.querySelectorAll("*").forEach(n => {			
			n.style.cursor = "none";
			n.style.pointerEvents = "none";
		});

		this.svg.querySelectorAll("[devs-model-id]").forEach(n => {
			n.style.cursor = "pointer";
			n.style.pointerEvents = "all"

			n.addEventListener("mousemove", this.on_svg_mousemove.bind(this, n));
			n.addEventListener("click", this.on_svg_click.bind(this, n));
			n.addEventListener("mouseout", this.on_svg_mouseout.bind(this, n));
		});
	}

	get_link_svg_nodes(l) {
		var selector = `[devs-link-mA=${l.model_a.id}][devs-link-pA=${l.port_a.name}]`;

		return Array.from(this.svg.querySelectorAll(selector));
	}

	get_port_svg_nodes(m, p) {
		var selector = `[devs-model-id=${m.id}],[devs-port-model=${m.id}][devs-port-name=${p.name}]`;

		return Array.from(this.svg.querySelectorAll(selector));
	}

	load_origin_svg_nodes() {
		this.simulation.models.forEach(m => {
			m.ports.forEach(p => {
				if (!this._svg.origins[m.id]) this._svg.origins[m.id] = {};

				var nodes = this.get_port_svg_nodes(m, p);

				m.get_port_links(p).forEach(l => nodes = nodes.concat(this.get_link_svg_nodes(l)));

				this._svg.origins[m.id][p.name] = nodes;
			});
		});
	}

	load_destination_svgs() {
		this.simulation.models.forEach(m => {
			m.ports.forEach(p => {
				if (!this._svg.dests[m.id]) this._svg.dests[m.id] = {};

				var links = m.get_port_links(p);

				for (var i = 0; i < m.get_port_links(p).length; i++) {
					var mB = links[i].model_b;
					var pB = links[i].port_b;

					if (mB.type == "atomic") continue;

					links = links.concat(mB.get_port_links(pB));
				}

				var svg = [];

				links.forEach(l => {
					svg = svg.concat(this.get_link_svg_nodes(l));
					svg = svg.concat(this.get_port_svg_nodes(l.model_b, l.port_b));
				});

				this._svg.dests[m.id][p.name] = Array.from(svg);
			});
		});
	}

	get_destination(model, port) {
		return this._svg.dests[model.id][port.name];
	}

	get_origin(model, port) {
		return this._svg.origins[model.id][port.name];
	}

	on_svg_mousemove(node, ev) {
		var id = node.getAttribute("devs-model-id");
		var model = this.simulation.get_model(id);

		if (!model) return;

		this.emit("mousemove", { x:ev.pageX, y:ev.pageY, model:model, svg:ev.target });
	}

	on_svg_mouseout(node, ev) {
		var id = node.getAttribute("devs-model-id");

		this.emit("mouseout", { x:ev.pageX, y:ev.pageY, model:this.simulation.get_model(id), svg:ev.target });
	}

	on_svg_click(node, ev) {	
		var id = node.getAttribute("devs-model-id");

		this.emit("click", { x:ev.pageX, y:ev.pageY, model:this.simulation.get_model(id), svg:ev.target });
	}

	resize() {		
		this.size = Dom.geometry(this.elems.diagram);

		var pH = 30;
		var pV = 30;

		this.elems.canvas.setAttribute('width', this.size.w);	
		this.elems.canvas.setAttribute('height', this.size.h);
	}

	draw_to_canvas(node) {
		var serializer = new XMLSerializer();
		var source = serializer.serializeToString(node);
		var cv = this.elems.canvas;

		// create a file blob of our SVG.
		var blob = new Blob([source], { type: 'image/svg+xml;charset=utf-8' });
		var url = window.URL.createObjectURL(blob);

		var ctx = cv.getContext('2d');
		var img = new Image();

		img.onload = function() {
			ctx.fillStyle = "#f9f9f9";
			ctx.fillRect(0, 0, cv.getAttribute("width"), cv.getAttribute("height"));
			ctx.drawImage(img, 0, 0, cv.getAttribute("width"), cv.getAttribute("height"));

			window.URL.revokeObjectURL(url);
		}

		img.src = url;
	}

	draw(messages) {		
		this.reset();

		messages.forEach((m) => this.draw_y_message(m));

		this.draw_to_canvas(this.svg);
	}

	draw_y_message(message) {
		var p = message.port;
		var m = message.model;

		this.add_css(this.get_origin(m, p), ["highlighted", "origin"]);

		this.add_css(this.get_destination(m, p), ["highlighted"]);		
	}

	add_css(nodes, css) {		
		nodes.forEach(node => {
			css.forEach(c => node.classList.add(c));
		});
	}

	remove_css(nodes, css) {
		nodes.forEach(node => {
			css.forEach(c => node.classList.remove(c));
		});
	}

	reset() {		
		for (var m in this._svg.dests) {
			for (var p in this._svg.dests[m]) {
				this.remove_css(this._svg.dests[m][p], ["highlighted", "origin"]);
			}
		}

		for (var m in this._svg.origins) {
			for (var p in this._svg.origins[m]) {
				this.remove_css(this._svg.origins[m][p], ["highlighted", "origin"]);
			}
		}
	}

	html() {
		return "<div>" +
				   "<div handle='diagram' class='diagram-widget'></div>" +
				   "<canvas handle='canvas' class='diagram-canvas hidden'></canvas>" +
			   "</div>";
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Net from '../../tools/net.js';
import GIS from '../gis/gis.js';
import Automator from '../../components/automator.js';
import Reader from '../../components/chunk-reader.js';
import AppConfig from '../../components/config.js';

export default Core.templatable("Api.Widget.GIS.Auto", class AutoGIS extends Automator { 

	get canvas() { return this.widget.canvas; }

	constructor(node, simulation, options, files) {
		if (!options) throw new Error("No options provided for the GIS widget");

		super(new GIS(node), simulation);

		this.config = options;		

		this.load(options, files).then(data => {
			this.widget.load(options, data, simulation).then(this.on_map_ready.bind(this), this.on_map_error.bind(this));
		});

		this.simulation.on("move", this.on_simulation_move.bind(this));
		this.simulation.on("jump", this.on_simulation_jump.bind(this));
	}

	load(config, files) {
		// Load all geojson data layers contained in visualization.json
		var defs = config.layers.map(l => {
			if (l.file) {
				var f = files.find((f) => f.name == l.file);

				if (f) return Reader.read(f, (json) => JSON.parse(json));

				if (!AppConfig.URLs.models) { 
					throw new Error(`Required file for layer ${l.id} was not provided by the user. Unable to fall back on server-side load, Core root is not configured.`);
				}

				l.url = AppConfig.URLs.models + "/" + l.file;
			}

			return Net.json(l.url);	
		});

		return Promise.all(defs);
	}

	on_map_ready(ev) {		
		this.config.on("change", this.on_settings_change.bind(this));

		this.emit("ready", { view:this });
	}

	redraw() {
		this.widget.draw(this.simulation.state.data);
	}

	resize() {

	}

	on_simulation_jump(ev) {
		this.widget.draw(ev.state.data);
	}

	on_simulation_move(ev) {
		var data = {};

		ev.frame.state_messages.forEach(t => data[t.model.id] = t.value);

		this.widget.draw(data);
	}

	on_settings_change(ev) {			

	}

	on_map_error(error) {
		alert(error.toString());
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Style from '../../tools/style.js'
import Widget from '../../base/widget.js';
import VariableSelect from './variable-select.js';
import Content from './popup-content.js';

import Map from '../../components/ol/map.js'
import Legend from "../../components/ol/legend.js";

export default Core.templatable("Api.Widget.GIS", class GIS extends Widget { 

	get canvas() { 
		return this.elems.map.querySelector(".ol-layer").firstChild; 
	}

	get current() {
		return this.elems.variable_select.selected;
	}

	constructor(node) {
		super(node);
	}

	load(config, data, simulation) {
		// TODO: This shouldn't be here but, just trying to get it done for now. I'll come back later.
		this.simulation = simulation;
		this.config = config;
		this.data = data;

		var d = Core.defer();

		var basemap1 = Map.basemap_osm(this.config.basemap == "openstreetmap");
		var basemap2 = Map.basemap_satellite(this.config.basemap == "satellite");

		this.map = new Map(this.elems.map, [basemap1, basemap2]);

		if (config.view) this.map.set_view(config.view.center, config.view.zoom);

		else this.map.set_view([-75.7, 45.3], 10);

		// Add layers to the map according to the loaded geojson data
		data.forEach((data, i) => {
			var style = config.styles.find(s => s.id == config.layers[i].style);

			this.load_layer(data, config.layers[i], style);
		});

		this.map.on("click", this.on_map_click.bind(this));
		this.map.on("render-complete", this.on_map_render_complete.bind(this, d));

		this.popup_content = new Content(this.map.popup.content, { 
			get_title: null,
			get_content: this.get_content.bind(this)
		});

		return d.promise;
	}

	load_layer(data, layerConfig, styleConfig) {
		data.name = layerConfig.id;

		var layer = this.map.add_geojson_layer(layerConfig.id, data);

		layer.set('visible', false);

		var style = Style.from_json(layerConfig.type, styleConfig);

		layer.setStyle(style.symbol());
	}

	on_map_render_complete(d, ev) {		
		// Show geo layers and draw simulation state
		for (var id in this.map.layers) {
			this.map.layers[id].set('visible', true);
		}

		// Prepare simulation styles, set first one as currently selected, 
		// update the variable selector to reflect the current property being coloured
		this.prepare_simulation_visualization();

		// Add variable-select widget
		this.elems.variable_select.add_selectors(this.config.layers, this.config.variables);
		this.elems.variable_select.on("change", this.on_variable_select_change.bind(this));

		this.map.add_control(this.elems.variable_select.control);

		this.draw(this.simulation.state.data);

		this.add_legend();
		this.add_layer_switcher();

		d.Resolve(this.map);
	}

	draw(data) {
		if (!this.map) return;

		for (var id in this.current) {
			var features = this.map.layer_features(id);
			var variable = this.current[id];

			features.forEach(f => {
				var id = f.getProperties()[variable.layer.join];
				var d = data[id];

				if (d != null) f.setStyle(variable.style.symbol(data[id]));
			});
		}

	}

	add_legend(){	
		if (this.legend) this.map.remove_control(this.legend.control);

		this.legend = new Legend(this.current);

		for (var id in this.current) this.legend.add_legend(this.current[id]);

		this.map.add_control(this.legend.control);
	}

	add_layer_switcher() {
		var ls = new ol.control.LayerSwitcher({ groupSelectStyle: "group" });

		this.map.add_control(ls);
	}

	prepare_simulation_visualization() {
		var stats = Style.statistics(this.simulation);

		this.config.variables.forEach(s => {
			s.layer = this.config.layers.find(l => l.id == s.layer);
			s.style = Style.from_json(s.layer.type, s);

			s.style.bucketize(stats);
		});
	}

	on_variable_select_change(ev){		
		this.draw(this.simulation.state.data);

		this.add_legend();
	}

	on_map_click(ev) {
		this.reset_selected();

		this.map.show_popup(null);

		this.selected = this.get_selected(ev.features);

		if (!this.selected) return;

		this.highlight_selected();

		if (this.selected.length == 0) return;

		this.popup_content.fill(this.selected);

		this.map.popup.setPosition(ev.coordinates);
	}

	get_selected(features) {
		var selected = features.filter(f => {
			return !!this.current[f.layer];			
		});

		return selected.length > 0 ? selected : null;
	}

	get_content(s) {
		var variable = this.current[s.layer];

		if (!variable) return;

		// state messages
		var props = s.feature.getProperties();
		var id = props[variable.layer.join];
		var model = this.simulation.get_model(id);
		var data = this.simulation.state.get_value(model);

		var line = `<div class='title'>${variable.layer.label}</div>`;

		line += `<ul class='properties'><li>Attributes</li>`;

		variable.layer.fields.forEach(f => line += `<li>${f}: ${props[f]}</li>`);

		line += `</ul>`;
		line += `<ul class='state-message'><li>State</li>`;

		for (var p in data) line += `<li>${p}: ${data[p]}</li>`

		line += `</ul>`;

		// output messages
		var tY = this.simulation.current_frame.output_messages.filter(t => t.model.id == id);

		tY.forEach(t => {
			line += `<ul class='output-message'><li>Output</li>`;

			for (var p in t.value) line += `<li>${p}: ${t.value[p]}</li>`

			line += `</ul>`;
		});

		return line;
	}

	reset_selected() {
		if (!this.selected) return;

		this.selected.forEach(s => {
			var variable = this.current[s.layer];

			if (!variable) return;

			var id = s.feature.getProperties()[variable.layer.join];
			var model = this.simulation.get_model(id);
			var data = this.simulation.state.get_value(model);
			var style = variable.style.symbol(data);

			s.feature.setStyle(style);
		});
	}

	highlight_selected() {		
		if (!this.selected) return;

		this.selected.forEach(s => {
			// TODO: There's an issue here, highlight style has to be geometry type specific.
			var json = this.config.styles.find(s => s.id == "highlight");
			var type = s.feature.getGeometry().getType().toLowerCase();
			var style = Style.get_style(type, json);

			s.feature.setStyle(style);
		});
	}

	html() {
		return "<div class='map-container'>" + 
				   "<div handle='map' class='map'></div>" +
				   "<div handle='variable_select' widget='Widgets.GIS.VariableSelect'></div>" +
			   "</div>";
	}

});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Widget from '../../base/widget.js';

export default Core.templatable("Api.Widget.GIS.PopupContent", class PopupContent extends Widget { 
	
	get size() { return this.features.length; } 
	
	constructor(node, options) {
		super(node);
		
		this.get_content = options.get_content;
		this.get_title = options.get_title;
		
		this.fill(options.features || [])
		
		this.elems.prev.addEventListener("click", this.prev_page.bind(this));
		this.elems.next.addEventListener("click", this.next_page.bind(this));
	}
	
	fill(features) {		
		this.features = features;
		this.i = 0;
		
		if (this.size > 0) this.set_page(this.i);
		
		Dom.toggle_css(this.elems.button_container, 'hidden', this.size == 1);
	}
	
	set_page(i) {
		this.elems.page.innerHTML = `${i + 1} of ${this.size}`;
		
		Dom.empty(this.elems.props);
		
		var content = this.get_content(this.features[i]);
		
		if (content instanceof HTMLElement) Dom.place(content, this.elems.props);
		
		else this.elems.props.innerHTML = content;
		
		if (!this.get_title) return;
		
		this.elems.title.innerHTML = this.get_title(this.features[i]);
	}
	
	next_page() {
		this.i = (this. i + 1) % this.size;
		this.set_page(this.i);
	}
	
	prev_page() {
		this.i = (this.i + this.size - 1) % this.size;
		this.set_page(this.i);
	}
	
	html() {
		return "<div handle='root' class='popup-content'>" + 
				  "<div handle='title' class='title'></div>" +
				  "<div handle='props'></div>" +
				  "<div handle='button_container' class='button-container'>" + 
					 "<button handle='prev' class='button previous'><</button>" +
					 "<button handle='next' class='button next'>></button>" +
					 "<span handle='page'></span>" + 
				  "</div>" +
			   "</div>";
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Widget from '../../base/widget.js';
import Select from '../../ui/select.js';

export default Core.templatable("Widgets.GIS.VariableSelect", class GIS extends Widget { 

	get selected() { return this._selected; }

	get control() { return this._control; }

	constructor(node) {
		super(node);

		this._selected = {};

		this._control = new ol.control.Control({ element: this.elems.variable_select_container });
	}

	add_selectors(layers, variables) {
		// Add map widgets, some of them require everything to be ready		
		layers.forEach(l => {
			var filtered = variables.filter(s => s.layer.id == l.id);

			if (filtered.length == 0) return; 

			this.add_selector(l, filtered);
		});
	}

	add_selector(layer, variables) {
		var title = this.nls("title_variable_select")
		var select = new Select(this.elems.variable_select_container);

		variables.forEach((v, i) => {			
			select.add(v.name, null, { layer:layer, variable:v });
		});

		select.value = 0;

		this.selected[layer.id] = variables[0];

		select.on("change", this.on_variable_select_change.bind(this));
	}

	on_variable_select_change(ev){
		this.selected[ev.item.layer.id] = ev.item.variable;

		this.emit("change", { selected:this.selected });
	}

	html() {
		return "<div handle='variable_select_container' class='variable-select-container custom-control'>" + 
				   "<label>nls(label_variable_select)</label>" +
				"</div>";
	}

	localize(nls) {
		super.localize(nls);

		nls.add("label_variable_select", "en", "Select layer variables:");
		nls.add("label_variable_select", "fr", "Choisissez les variables:");
		nls.add("title_variable_select", "en", "Select a simulation variable to display on the map");
		nls.add("title_variable_select", "fr", "Choisissez une variable de simulation à afficher sur la carte");
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Tooltip from '../../ui/tooltip.js';
import Automator from '../../components/automator.js';
import Grid from '../grid/grid.js';

export default Core.templatable("Api.Widget.Grid.Auto", class AutoGrid extends Automator { 

	get canvas() { return this.widget.canvas; }

	constructor(node, simulation, options) {
		if (!options) throw new Error("No options provided for the Grid widget");

		super(new Grid(node), simulation);

		this.options = options;

		this.widget.dimensions = this.simulation.dimensions;
		this.widget.columns = options.columns;
		this.widget.spacing	= options.spacing;
		this.widget.layers	= options.layers;
		this.widget.styles	= options.styles;

		this.attach_handlers(options);

		this.tooltip = new Tooltip();
	}

	attach_handlers(options) {
		var h = [];

		if (options.hoverEnabled != false) h.push(this.widget.on("mousemove", this.on_mousemove.bind(this)));
		if (options.hoverEnabled != false) h.push(this.widget.on("mouseout", this.on_mouseout.bind(this)));
		if (options.clickEnabled != false) h.push(this.widget.on("click", this.on_click.bind(this)));

		h.push(this.simulation.on("move", this.on_simulation_move.bind(this)));
		h.push(this.simulation.on("jump", this.on_simulation_jump.bind(this)));

		options.on("change", this.on_settings_change.bind(this));
		options.on("change:styles", this.on_settings_styles_change.bind(this));
		options.on("change:layers", this.on_settings_layers_change.bind(this));

		this.handle(h);
	}

	resize() {
		var n = this.widget.layers.length;
		var size = this.options.canvas_size(this.simulation, n);

		this.widget.container.style.width = size.width + "px";
		this.widget.container.style.height = size.height + "px";	
	}

	redraw() {
		this.widget.resize();

		this.widget.draw_state(this.simulation.state, this.simulation);
	}

	on_settings_change(ev) {			
		var check = ["height", "width", "columns", "spacing", "aspect", "layers"];

		if (check.indexOf(ev.property) == -1) return;

		this.widget.columns = this.options.columns;
		this.widget.spacing = this.options.spacing;
		this.widget.layers = this.options.layers;

		this.resize();
		this.redraw();
	}

	on_settings_styles_change(ev) {			
		this.redraw();
	}

	on_settings_layers_change(ev) {			
		this.redraw();
	}

	on_simulation_move(ev) {		
		var s = this.simulation;

		this.widget.draw_changes(ev.frame, s);
	}

	on_simulation_jump(ev) {
		var s = this.simulation;

		this.widget.draw_state(s.state, s);
	}

	on_simulation_palette_change(ev) {
		var s = this.simulation;

		this.widget.draw_state(s.state, s);
	}

	on_mousemove(ev) {
		var labels = [];

		ev.data.layer.ports.forEach(port => {
			var state = this.simulation.state.get_value([ev.data.x, ev.data.y, ev.data.layer.z]);
			var subs = [ev.data.x, ev.data.y, ev.data.layer.z, state[port], port];

			labels.push(this.nls("Grid_Tooltip_Title", subs));

			this.tooltip.show(ev.x + 20, ev.y);
		});

		this.tooltip.content = labels.join("<br>");
	}

	on_mouseout(ev) {
		this.tooltip.hide();
	}

	on_click(ev) {
		var id = [ev.data.x, ev.data.y, ev.data.z];

		if (this.simulation.is_selected(id)) {
			this.simulation.deselect(id);

			// TODO: This is nasty. Maybe just store the original color along with the id.
			var port = ev.data.layer.ports[ev.data.layer.ports.length - 1];
			var value = this.simulation.state.get_value(id);
			var scale = this.widget.styler.get_scale(ev.data.layer.style);
			var color = this.widget.styler.get_color(scale, value[port]);

			this.widget.draw_cell_border(ev.data.x, ev.data.y, ev.data.k, color);
		} 

		else {
			this.simulation.select(id);

			this.widget.draw_cell_border(ev.data.x, ev.data.y, ev.data.k, 'rgb(255,0,0)');
		}
	}

	localize(nls) {
		super.localize(nls);

		nls.add("Grid_Tooltip_Title", "en", "The state of cell <b>({0}, {1}, {2})</b> on port <b>{4}</b> is <b>{3}</b>.");
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';
import Widget from '../../base/widget.js';
import Styler from '../../components/styler.js';

const STROKE_WIDTH = 2;
const DEFAULT_COLOR = "#fff";

export default Core.templatable("Api.Widget.Grid", class Grid extends Widget { 

	get canvas() { return this.elems.canvas; }

	set dimensions(value) { this._dimensions = value; }
	get dimensions() { return this._dimensions; }

	set columns(value) { this._columns = value; }
	get columns() { return this._columns; }

	set spacing(value) { this._spacing = value; }
	get spacing() { return this._spacing; }

	set layers(value) { 
		this._layers = value; 
		this._index = {};

		this._layers.forEach((l, i) => {			
			if (!this._index.hasOwnProperty(l.z)) this._index[l.z] = {};

			l.ports.forEach(p => {
				if (!this._index[l.z].hasOwnProperty(p)) this._index[l.z][p] = [];

				this._index[l.z][p].push(l);
			});
		});
	}

	get layers() { return this._layers; }

	set styles(value) { 
		this._styles = value;
		this._styler = new Styler(value);
	}

	get styles() { return this._styles; }
	get styler() { return this._styler; }

	constructor(container) {
		super(container);

		this._cell = { w:null, h:null };
		this._dimensions = null;
		this._columns = null;
		this._spacing = null;
		this._size = null;
		this._styles = null;
		this._layers = [];
		this._grids = [];

		this._ctx = this.elems.canvas.getContext("2d");

		this.elems.canvas.addEventListener("mousemove", this.on_canvas_mousemove.bind(this));
		this.elems.canvas.addEventListener("mouseout", this.on_canvas_mouseout.bind(this));
		this.elems.canvas.addEventListener("click", this.on_canvas_click.bind(this));
	}

	html() {
		return "<div class='grid-widget'>" + 
				  "<div handle='canvas_container' class='grid-canvas-container'>" +
					"<canvas handle='canvas' class='grid-canvas'></canvas>" +
				  "</div>" + 
			   "</div>";
	}

	get_rows(columns, layers) {
		return Math.ceil(layers.length / columns) ;
	}

	resize() {
		this._size = Dom.geometry(this.elems.canvas_container);

		// Number of columns and rows
		this._layout = {
			columns : this.columns,
			rows : this.get_rows(this.columns, this.layers)
		}

		// Size of one layer drawn, only used to determine cell size, shouldn't be used after
		var layer = {
			w : (this._size.w - (this._layout.columns * this.spacing - this.spacing)) / this._layout.columns,
			h : (this._size.h - (this._layout.rows * this.spacing - this.spacing)) / this._layout.rows
		}

		// Size of a cell
		this._cell = {
			w : Math.floor(layer.w / this.dimensions.x),
			h : Math.floor(layer.h / this.dimensions.y)
		}

		// Total effective size of drawing space 
		this._total = {
			w : (this._cell.w * this.dimensions.x) * this._layout.columns + this._layout.columns * this.spacing - this.spacing,
			h : (this._cell.h * this.dimensions.y) * this._layout.rows + this._layout.rows * this.spacing - this.spacing
		}

		// Determine offset w, h to center grid as much as possible
		this._margin = {
			w : (this._size.w - this._total.w) / 2,
			h : (this._size.h - this._total.h) / 2
		}

		this._grids = this.layers.map((l, i) => {	
			var row = Math.floor(i / this._layout.columns);
			var col = i - (row * this._layout.columns);

			var x1 = col * (this.dimensions.x * this._cell.w + this.spacing);
			var y1 = row * (this.dimensions.y * this._cell.h + this.spacing);
			var x2 = x1 + this._cell.w * this.dimensions.x;
			var y2 = y1 + this._cell.h * this.dimensions.y;

			return { x1:x1, y1:y1, x2:x2, y2:y2, z:l.z } 
		}) 

		this.elems.canvas.style.margin = `${this._margin.h}px ${this._margin.w}px`;		

		// Redefine with and height to fit with number of cells and cell size
		this.elems.canvas.width = this._total.w;	
		this.elems.canvas.height = this._total.h;	
	}

	// TODO : grid shouldn't use simulation object maybe?
	draw(state, simulation) {
		if (this.dimensions) this.draw_state(state, simulation);

		else this.default(DEFAULT_COLOR);
	}

	clear() {
		this._ctx.clearRect(0, 0, this._size.w, this._size.h);
	}

	default(color) {
		this._ctx.fillStyle = color;
		this._ctx.fillRect(0, 0, this._size.w, this._size.h);
	}

	// TODO : grid shouldn't use simulation object maybe?
	draw_state(state, simulation) {
		for (var i = 0; i < this.layers.length; i++) {
			var l = this.layers[i];
			var scale = this.styler.get_scale(l.style);

			for (var x = 0; x < this.dimensions.x; x++) {
				for (var y = 0; y < this.dimensions.y; y++) {
					for (var p = 0; p < l.ports.length; p++) {
						var v = state.get_value([x, y, l.z]); // value of cell to draw
						var f = l.ports[p]; 

						var color = this.styler.get_color(scale, v[f]) || 'rgb(200, 200, 200)';

						this.draw_cell(x, y, i, color);
					}

					var id = x + "-" + y + "-" + l.z; // id of cell to draw

					if (simulation.is_selected(id)) this.draw_cell_border(x, y, i, 'rgb(255,0,0)');
				}
			}
		}
	}

	// TODO : grid shouldn't use simulation object maybe?
	draw_changes(frame, simulation) {
		for (var i = 0; i < frame.state_messages.length; i++) {
			var m = frame.state_messages[i];

			for (var f in m.value) {
				var layers = this._index[m.z] && this._index[m.z][f] || [];
				var v = m.value[f];

				for (var j = 0; j < layers.length; j++) {
					var l = layers[j];
					var scale = this.styler.get_scale(l.style);

					this.draw_cell(m.x, m.y, l.position, this.styler.get_color(scale, v));

					if (simulation.is_selected(m.coord)) this.draw_cell_border(m.x, m.y, l.position, 'rgb(255,0,0)');
				}
			}
		}
	}

	get_cell(clientX, clientY) {
		var rect = this.elems.canvas.getBoundingClientRect();

		var x = clientX - rect.left;
		var y = clientY - rect.top;

		var zero = null;

		for (var k = 0; k < this._grids.length; k++) {
			if (x < this._grids[k].x1 || x > this._grids[k].x2) continue;

			if (y < this._grids[k].y1 || y > this._grids[k].y2) continue;

			zero = this._grids[k];

			break;
		}

		if (!zero || zero.y2 == y) return null;

		x = x - zero.x1;
		y = y - zero.y1;

		// Find the new X, Y coordinates of the clicked cell
		var pX = x - x % this._cell.w;
		var pY = y - y % this._cell.h;

		return { x:pX / this._cell.w, y:pY / this._cell.h, z:zero.z, k:k, layer:this.layers[k] };
	}

	draw_cell(x, y, k, color) {			
		var zero = this._grids[k];

		var x = zero.x1 + x * this._cell.w;
		var y = zero.y1 + y * this._cell.h;

		this._ctx.fillStyle = color;
		this._ctx.fillRect(x, y, this._cell.w, this._cell.h);
	}

	draw_cell_border(x, y, k, color) {	
		var zero = this._grids[k];

		// Find the new X, Y coordinates of the clicked cell
		var pX = zero.x1 + x * this._cell.w;
		var pY = zero.y1 + y * this._cell.h;

		var dX = pX + (STROKE_WIDTH / 2);
		var dY = pY + (STROKE_WIDTH / 2);

		// Define a stroke style and width
		this._ctx.lineWidth = STROKE_WIDTH;
		this._ctx.strokeStyle = color;

		// Draw rectangle, add offset to fix anti-aliasing issue. Subtract from height and width 
		// to draw border internal to the cell
		this._ctx.strokeRect(dX, dY, this._cell.w - STROKE_WIDTH, this._cell.h - STROKE_WIDTH);
	}

	on_canvas_click(ev) {		
		var data = this.get_cell(ev.clientX, ev.clientY);

		if (!data) return;

		this.emit("click", { x:ev.pageX, y:ev.pageY, data:data });
	}

	on_canvas_mousemove(ev) {				
		var data = this.get_cell(ev.clientX, ev.clientY);

		if (!data) return;

		this.emit("mousemove", { x:ev.pageX, y:ev.pageY, data:data });
	}

	on_canvas_mouseout(ev) {		
		this.emit("mouseout", { x:ev.pageX, y:ev.pageY });
	}
});

'use strict';

import Core from '../../tools/core.js';
import Dom from '../../tools/dom.js';

import Widget from '../../base/widget.js';
import Reader from '../../components/chunk-reader.js';

export default Core.templatable("Api.Widget.Linker", class Linker extends Widget { 

    get svg() { return this.elems.svg_content; }

	get svg_file() { 
		return new File([this.svg.innerHTML], "diagram.svg", { type:"image/svg+xml", endings:'native' }); 
	}

	get current() { return this._current; }

	constructor(container) {
		super(container);
	}

	async initialize(simulation, diagram) {
		this._current = {
			button: null,
			page: null,
			card: null
		}

		this.simulation = simulation;

		this.options = await this.make_options(simulation, diagram);

		this.load_svg(this.options.diagram);
		this.make_pages(this.options.pages);

		this.elems.chk_thick.addEventListener('click', this.on_linker_thicken.bind(this));
		this.elems.btn_clear.addEventListener('click', this.on_linker_clear.bind(this));

		this.options.pages.forEach(c => {
			c.button = Dom.create("button", { type: "button", className: "m-1 inactive", innerHTML: c.caption }, this.elems.buttons);

			c.button.addEventListener('click', (ev) => this.change_page(c));
		});

		this.change_page(this.options.pages[0]);
	}

	async make_options(simulation, diagram) {
		var diagram = await Reader.read_as_text(diagram);
		var ports = [];
		var links = [];

		simulation.models.forEach(m => {
			m.ports.forEach(p => ports.push({ model:m, port:p }));
		});

		simulation.models.forEach(m => links = links.concat(m.links));

		return {
			diagram: diagram,
			selector : Linker.SVG_FORMAT.DRAW_IO,
			pages: [{
				caption: 'Models',
				empty: 'No models found in the structure file.',
				label: d => `<b>${d.id}</b>`,
				items: simulation.models,
				attrs: {
					"devs-model-id" : d => d.id
				}
			}, {
				caption: 'Output ports',
				empty: 'No output ports found in the structure file.',
				label: d => `<div><b>${d.port.name}</b> @ <b>${d.model.id}</b></div>`,
				items: ports,
				attrs: {
					"devs-port-model" :  d => d.model.id,
					"devs-port-name" : d => d.port.name
				}
			}, {
				caption: 'Links',
				empty: 'No links found in the structure file.',
				label: d => `<div><b>${d.port_a.name}</b> @ <b>${d.model_a.id}</b> to</div><div><b>${d.port_b.name}</b> @ <b>${d.model_b.id}</b></div>`,
				items: links,
				attrs: {
					"devs-link-mA" : d => d.model_a.id,
					"devs-link-pA" : d => d.port_a.name
				}
			}]
		}
	}

    load_svg(diagram) {
		var selector = Linker.SVG_FORMAT.DRAW_IO;

		this.svg.innerHTML = diagram;

		this.reset_pointer_events();

		this.svg.querySelectorAll(selector).forEach((n, index) => {
			n.addEventListener('click', this.on_svg_click.bind(this), false);

			n.style.cursor = "pointer";
			n.style.pointerEvents = "all"
		});
    }

	reset_pointer_events() {
		this.svg.querySelectorAll('*').forEach(n => {
			n.style.cursor = "none";
			n.style.pointerEvents = "none"
		});
	}

	make_pages(pages) {
		pages.forEach(page => {
			page.items = page.items.map(item => this.make_item(page, item));
		});
	}

	make_item(page, item) {
		var item = { data:item, attrs:{}, node:null, svg:[] }

		for (var attr in page.attrs) item.attrs[attr] = page.attrs[attr](item.data);

		item.node = Dom.create("div", { innerHTML:page.label(item.data), className:"card m-1 dwl-pointer dwl-card" });

		Dom.create("i", { className:"fas fa-exclamation-triangle warning-icon" }, item.node);

		item.node.addEventListener('click', this.on_card_click.bind(this, item), false);

		item.svg = this.get_associations(item.attrs);

		return item;
	}

	get_associations(attrs) {
		var selector = "";

		for (var attr in attrs) selector += `[${attr}=${attrs[attr]}]`

		return Array.from(this.svg.querySelectorAll(selector));
	}

	change_page(page) {
		Dom.empty(this.elems.cards);

		if (this.current.button) Dom.add_css(this.current.button, 'inactive');

		this.current.button = page.button;

		Dom.remove_css(this.current.button, 'inactive');

        this.clear_svg();

		this.current.page = page;

		if (page.items.length == 0) {
			return Dom.create("p", { innerHTML:page.empty }, this.elems.cards);
		}

		page.items.forEach(item => {
			Dom.place(item.node, this.elems.cards);

			Dom.toggle_css(item.node, 'hide-warning', item.svg.length > 0);
		});
	}

	clear_svg() {
		if (!this.current.card) return;

		Dom.remove_css(this.current.card.node, 'dwl-highlight-card');
	}

	clear_cards() {
		if (!this.current.card) return;

		this.current.card.svg.forEach(n => n.classList.remove('dwl-selected'));
	}

	refresh_warnings() {
		this.options.pages.forEach(p => {
			p.items.forEach(card => {
				Dom.toggle_css(card.node, 'hide-warning', card.svg.length > 0);
			});
		});
	}

	reset_thickness() {
		this.elems.chk_thick.checked = false;

		this.svg.classList.toggle("dwl-thick", false);	
	}

	clear() {
		this.clear_svg();
		this.clear_cards();
		this.refresh_warnings();
	}

	reset() {
		this.clear();
		this.reset_pointer_events();
		this.reset_thickness();
	}

	on_svg_click(ev) {
		var card = this.current.card;

		if (!card) return;

		var i = card.svg.indexOf(ev.target);

		if (i != -1) {
			ev.target.classList.remove('dwl-selected');

			card.svg.splice(i, 1);

			for (var attr in card.attrs) ev.target.removeAttribute(attr);
		}

		else {
			ev.target.classList.add('dwl-selected');

			card.svg.push(ev.target);

			for (var attr in card.attrs) ev.target.setAttribute(attr, card.attrs[attr]);
		}

		Dom.toggle_css(card.node, 'hide-warning', card.svg.length > 0);
	}

	on_card_click(card, ev) {
		this.clear();

		if (this.current.card == card) this.current.card = null;

		else {
			this.current.card = card;

			Dom.add_css(card.node, 'dwl-highlight-card');

			card.svg.forEach(a => a.classList.add('dwl-selected'));
		}
	} 

	on_linker_thicken(ev) {
		this.svg.classList.toggle("dwl-thick", ev.target.checked);	
	}

	on_linker_clear(ev) {
		debugger;
		// TODO: There are a few bugs here. The attributes get cleared correctly but the element on the right hand side
		// stays highlighted. Afterwards, when animating the diagram, the couplings still get highlighted.
		this.options.pages.forEach(p => {
			p.items.forEach(card => {
				card.svg.forEach(svg => {
					for (var attr in card.attrs) svg.removeAttribute(attr);
				});

				card.svg = [];
			});
		});

		this.clear();
	}

	html() {
		return `<div class="linker-widget d-flex flex-row h-100 w-100">` + 
			      `<div id="json-container" class="d-flex flex-column card me-1 h-100 w-100">` + 
					 `<div handle="buttons" class="p-3"></div>` + 
					 `<div handle="cards" id="cards" class="h-100 d-flex flex-row flex-wrap align-content-start justify-content-center overflow-auto">` + 
					    `<div handle="svg_content"></div>`+					 
					 `</div>` + 
					 `<p class="m-2">NOTE: Input ports will be automatically associated.</p>` + 
				  `</div>` + 
				  `<div id="svg-container" class="card ms-1 h-100 w-100 position-relative">` + 
				     `<div class="p-3 end-0">` + 
						`<div class="m-1 float-start">` + 
						   `<input handle="chk_thick" id="dwl-thick-chk" class="m-1 dwl-pointer align-middle" type="checkbox" title="Thicken line strokes for easier interaction."/>` +
						   `<label class="m-1 dwl-pointer align-middle" title="Thicken line strokes for easier interaction." for="dwl-thick-chk">Thicker line strokes</label>` + 
						`</div>` +
						`<button handle="btn_clear" type="button" class="m-1 float-end" data-button-type="clear" title="Remove all associations.">Clear</button>` +
					 `</div>` +
					 `<div handle="svg_content" id="svg-content"></div>` +
				  `</div>` + 
			   `</div>`;
	}

	localize(nls) {
		super.localize(nls);
	}

	static get SVG_FORMAT() {
		return {
			DRAW_IO: 'foreignObject div > div > div, rect:not([fill="none"][stroke="none"]), circle, ellipse, path, polygon, text'
		}
	}
});

